INSERT INTO `baohologo` (`id`, `demuc`, `tieude`, `anh`, `thoigiandang`, `noidung`, `taikhoanluatsu_id`) VALUES
(1, 'Nhãn hiệu Logo độc quyền', 'Bảo hộ logo, nhãn hiệu sử dụng địa danh nước ngoài tại Việt Nam được không?', '12-1.png', '2023-03-13 10:20:06', '<p>Địa danh l&agrave; t&ecirc;n của một khu vực, địa phương, v&ugrave;ng&nbsp; địa&nbsp; l&yacute;&nbsp; cụ&nbsp; thể (v&ugrave;ng). Đăng k&yacute; bảo hộ logo, nh&atilde;n hiệu c&oacute; chứa địa danh thường được sử dụng với&nbsp; t&ecirc;n của sản phẩm được sản xuất tại khu vực, địa phương tương ứng với địa danh đ&oacute; (v&ugrave;ng&nbsp; mang&nbsp; địa danh).</p>\r\n\r\n<p>Doanh nghiệp/c&aacute; nh&acirc;n bảo hộ logo, nh&atilde;n hiệu thường sẽ cho th&ecirc;m t&ecirc;n địa danh sử dụng trong logo, nh&atilde;n hiệu như một yếu tố m&ocirc; tả về nguồn gốc xuất xứ sản phẩm/dịch vụ của m&igrave;nh. Đặc biệt, doanh nghiệp/ c&aacute; nh&acirc;n Việt Nam hay sử dụng t&ecirc;n địa danh nước ngo&agrave;i trong logo, nh&atilde;n hiệu để đăng k&yacute; cho sản phẩm, dịch vụ của m&igrave;nh (như: Tokyo deli, EL&rsquo;VA PARIS&hellip;&hellip;).<br />\r\nTuy nhi&ecirc;n, theo Khoản 5 Điều 73 Luật sở hữu tr&iacute; tuệ 2005 (sửa đổi 2009) th&igrave; việc đăng k&yacute; bải hộ logo, nh&atilde;n hiệu c&oacute; sử dụng t&ecirc;n địa danh nước ngo&agrave;i l&agrave; &ldquo;<em>Dấu hiệu l&agrave;m&nbsp;<strong>hiểu sai lệch, g&acirc;y nhầm lẫn</strong>&nbsp;hoặc c&oacute;&nbsp;<strong>t&iacute;nh chất lừa dối</strong>&nbsp;người ti&ecirc;u d&ugrave;ng về nguồn gốc xuất xứ, t&iacute;nh năng, c&ocirc;ng dụng, chất lượng, gi&aacute; trị hoặc c&aacute;c đặc t&iacute;nh kh&aacute;c của h&agrave;ng ho&aacute;, dịch vụ</em>&rdquo; sẽ kh&ocirc;ng được bảo hộ dưới dạng nh&atilde;n hiệu.</p>\r\n\r\n<p>Do đ&oacute;, khi doanh nghiệp/c&aacute; nh&acirc;n Việt Nam đăng k&yacute; bảo hộ logo, nh&atilde;n hiệu c&oacute; sử dụng t&ecirc;n địa danh nước ngo&agrave;i trong trường hợp n&ecirc;u tr&ecirc;n tại Cục sở hữu tr&iacute; tuệ Việt Nam sẽ Cục SHTT bị từ chối cấp văn bằng bảo hộ.<br />\r\nTuy nhi&ecirc;n, nếu doanh nghiệp/c&aacute; nh&acirc;n l&agrave; doanh nghiệp/người nước ngo&agrave;i khi đăng k&yacute; bảo hộ logo, nh&atilde;n hiệu c&oacute; sử dụng t&ecirc;n địa danh nước ngo&agrave;i vẫn c&oacute; thể được bảo hộ nếu thỏa m&atilde;n c&aacute;c điều kiện kh&aacute;c theo quy định ph&aacute;p luật.<br />\r\nV&iacute; dụ 1 số logo, nh&atilde;n hiệu trong trường hợp n&agrave;y được bảo hộ tại Việt Nam như sau: PARIS BLUES, TOKYO ROPE MFG. CO., LTD, Hi Seoul, &hellip;.</p>\r\n', 1),
(2, 'Nhãn hiệu Logo độc quyền', 'Tiêu chí đánh giá khả năng phân biệt của nhãn hiệu có dấu hiệu kết hợp', 'tai-xuong.png', '2023-03-13 10:38:22', '<p><span style=\"font-size:14px\">Căn cứ ph&aacute;p l&yacute;:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14px\">Điều 39 Th&ocirc;ng tư 01/2007/TT- BKHCN</span></li>\r\n	<li><span style=\"font-size:14px\">Luật sở hữu tr&iacute; tuệ 2005 (sửa đổi bổ sung 2009)</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\">Logo/Nh&atilde;n hiệu c&oacute; dấu hiệu kết hợp l&agrave; logo/nh&atilde;n hiệu c&oacute; cả dấu hiệu h&igrave;nh ảnh v&agrave; dấu hiệu chữ kết hợp th&agrave;nh một h&igrave;nh ảnh tổng thể.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"\" src=\"https://tuvanluathaiphong.com/wp-content/uploads/2019/07/images-300x154.jpg\" style=\"height:154px; width:300px\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Theo quy đinh ph&aacute;p luật th&igrave; một dấu hiệu kết hợp được coi l&agrave; c&oacute; khả năng ph&acirc;n biệt khi dấu hiệu chữ v&agrave; dấu hiệu h&igrave;nh kết hợp th&agrave;nh một tổng thể c&oacute; khả năng ph&acirc;n biệt, cụ thể:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14px\">Dấu hiệu chữ v&agrave; dấu hiệu h&igrave;nh đều c&oacute; khả năng ph&acirc;n biệt v&agrave; tạo th&agrave;nh tổng thể c&oacute; khả năng ph&acirc;n biệt;</span></li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\"><strong>Th&agrave;nh phần mạnh của nh&atilde;n hiệu</strong>&nbsp;(yếu tố t&aacute;c động mạnh v&agrave;o cảm gi&aacute;c người ti&ecirc;u d&ugrave;ng, g&acirc;y ch&uacute; &yacute; v&agrave; ấn tượng về nh&atilde;n hiệu khi quan s&aacute;t) l&agrave; dấu hiệu chữ hoặc dấu hiệu h&igrave;nh c&oacute; khả năng ph&acirc;n biệt, mặc d&ugrave; th&agrave;nh phần c&ograve;n lại kh&ocirc;ng c&oacute; hoặc &iacute;t c&oacute; khả năng ph&acirc;n biệt;</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Trường hợp dấu hiệu kết hợp gồm c&aacute;c dấu hiệu chữ v&agrave; dấu hiệu h&igrave;nh kh&ocirc;ng c&oacute; hoặc &iacute;t c&oacute; khả năng ph&acirc;n biệt nhưng&nbsp;<strong>c&aacute;ch thức kết hợp độc đ&aacute;o</strong>&nbsp;của c&aacute;c dấu hiệu đ&oacute; tạo ra một ấn tượng ri&ecirc;ng biệt th&igrave; tổng thể kết hợp đ&oacute; vẫn được coi l&agrave; c&oacute; khả năng ph&acirc;n biệt;</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">&nbsp;Dấu hiệu kết hợp gồm c&aacute;c th&agrave;nh phần chữ v&agrave; h&igrave;nh kh&ocirc;ng c&oacute; hoặc &iacute;t c&oacute; khả năng ph&acirc;n biệt nhưng tổng thể kết hợp đ&oacute; đ&atilde; đạt được&nbsp;<strong>khả năng ph&acirc;n biệt qua qu&aacute; tr&igrave;nh sử dụng</strong>. Nghĩa l&agrave; logo/nh&atilde;n hiệu đ&atilde; v&agrave; đang được sử dụng với chức năng nh&atilde;n hiệu v&agrave; được người ti&ecirc;u d&ugrave;ng biết đến một c&aacute;ch rộng r&atilde;i v&agrave; nhờ đ&oacute; nh&atilde;n hiệu đ&atilde; đạt được khả năng ph&acirc;n biệt đối với h&agrave;ng ho&aacute;, dịch vụ li&ecirc;n quan</span></h6>\r\n	</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\">Lưu &yacute;: Trong trường hợp n&agrave;y, nh&atilde;n hiệu đ&oacute; chỉ được thừa nhận l&agrave; c&oacute; khả năng ph&acirc;n biệt khi được thể hiện ở dạng đ&uacute;ng như dạng m&agrave; n&oacute; được sử dụng li&ecirc;n tục v&agrave; phổ biến trong thực tế.</span></p>\r\n', 1);

