--
-- Cấu trúc bảng cho bảng `dichvukhac`
--

CREATE TABLE `dichvukhac` (
  `id` int(11) NOT NULL,
  `demuc` varchar(500) NOT NULL,
  `tieude` varchar(500) NOT NULL,
  `anh` text NOT NULL,
  `thoigiandang` datetime NOT NULL DEFAULT current_timestamp(),
  `noidung` text NOT NULL,
  `taikhoanluatsu_id` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `dichvukhac`
--

INSERT INTO `dichvukhac` (`id`, `demuc`, `tieude`, `anh`, `thoigiandang`, `noidung`, `taikhoanluatsu_id`) VALUES
(1, 'Đăng ký đấu thầu', 'Dịch vụ Đăng ký thông tin trên mạng đấu thầu quốc gia tại Hải Phòng', 'THAU.jpg', '2023-03-13 10:20:56', '<p>Theo Điều 5 Luật đấu thầu 2013 quy định về Tư c&aacute;ch hợp lệ của nh&agrave; thầu, nh&agrave; đầu tư, trong đ&oacute;, 1 trong c&aacute;c điều kiện để c&uacute; tư kh&aacute;ch nh&agrave; thầu, nh&agrave; đầu tư l&agrave;&nbsp;<em>&ldquo;Đ&atilde; đăng k&yacute; tr&ecirc;n mạng đấu thầu quốc gia&rdquo;</em>&nbsp;_ quy định tại điểm d khoản 1 điều n&agrave;y.</p>\r\n\r\n<p>Như vậy, để c&oacute; tư c&aacute;ch nh&agrave; thầu, nh&agrave; đầu tư th&igrave; BẮT BUỘC phải Đ&Atilde; c&oacute; th&ocirc;ng tin tr&ecirc;n mạng đấu thầu quốc gia.</p>\r\n\r\n<ol>\r\n	<li><strong>Tr&igrave;nh tự thực hiện</strong></li>\r\n</ol>\r\n\r\n<p><em>Bước 1: Đăng k&yacute; th&ocirc;ng tin;</em></p>\r\n\r\n<p><em>Bước 2: Nhận m&atilde; ph&ecirc; duyệt v&agrave; số tham chiếu;</em></p>\r\n\r\n<p><em>Bước 3: Nhận chứng thư số;</em></p>\r\n\r\n<p><em>Bước 4: Đăng k&yacute; người sử dụng chứng thư số.</em></p>\r\n\r\n<ol>\r\n	<li><strong>Thời gian</strong>: 03 ng&agrave;y l&agrave;m việc</li>\r\n	<li>Hồ sơ:\r\n	<p><em>&ndash; Đối với B&ecirc;n mời thầu:</em></p>\r\n\r\n	<p><em>+ Đơn đăng k&yacute; B&ecirc;n mời thầu;</em></p>\r\n\r\n	<p><em>+ Bản sao c&ocirc;ng chứng quyết định th&agrave;nh lập của tổ chức đăng k&yacute;;</em></p>\r\n\r\n	<p><em>+ Bản sao chứng minh thư nh&acirc;n d&acirc;n của người đại diện ph&aacute;p luật của tổ chức đăng k&yacute;</em></p>\r\n\r\n	<p><em>&ndash; Đối với Nh&agrave; thầu:</em></p>\r\n\r\n	<p><em>+ Đơn đăng k&yacute; B&ecirc;n nh&agrave; thầu;</em></p>\r\n\r\n	<p><em>+ Bản sao c&ocirc;ng chứng giấy ph&eacute;p đăng k&yacute; kinh doanh của tổ chức đăng k&yacute;;</em></p>\r\n\r\n	<p><em>+ Bản sao chứng minh thư nh&acirc;n d&acirc;n của người đại diện ph&aacute;p luật của tổ chức đăng k&yacute;.</em></p>\r\n	</li>\r\n	<li><strong>Cơ quan c&oacute; thẩm quyền:</strong>&nbsp;Cục quản l&yacute; đấu thầu quốc gia</li>\r\n	<li><strong>Kết quả thực hiện thủ tục h&agrave;nh ch&iacute;nh</strong>:<em>C&oacute; t&ecirc;n trong cơ sở dữ liệu của hệ thống mạng đấu thầu quốc gia, được cấp chứng thư số tham gia nghiệp vụ đấu thầu qua mạng.</em></li>\r\n	<li><strong>Lệ ph&iacute;</strong>:\r\n	<p>B&ecirc;n nh&agrave; thầu: 550.000 đồng</p>\r\n\r\n	<p>B&ecirc;n mời thầu: Miễn ph&iacute;</p>\r\n	</li>\r\n</ol>\r\n\r\n<p>C&Ocirc;NG TY LUẬT CMA</p>\r\n\r\n<p>Địa chỉ: số 135 L&ecirc; Th&aacute;nh T&ocirc;ng, Ng&ocirc; Quyền, Hải Ph&ograve;ng</p>\r\n\r\n<p>Li&ecirc;n hệ: 0944.296.698</p>\r\n', 1),
(2, 'Giấy phép Lao động', 'Dịch vụ cấp giấy phép cho thuê lại lao động tại Hải Phòng', 'gege.jpg', '2023-03-13 10:39:20', '<ol>\r\n	<li><strong>Cơ sở ph&aacute;p l&yacute;</strong></li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Bộ luật Lao động năm 2012.</li>\r\n	<li>&nbsp;Nghị định số 29/2019NĐ-CP quy định chi tiết Khoản 3 Điều 54 của Bộ luật Lao động về việc cấp ph&eacute;p hoạt động cho thu&ecirc; lại lao động, việc k&yacute; quỹ v&agrave; danh mục c&ocirc;ng việc được thực hiện cho thu&ecirc; lại lao động.</li>\r\n	<li>Nghị định số 28/2020/NĐ-CP quy định xử phạt vi phạm h&agrave;nh ch&iacute;nh lĩnh vực lao động, bảo hiểm x&atilde; hội, đưa người Việt Nam đi lao động ở nước ngo&agrave;i.</li>\r\n</ul>\r\n\r\n<p><strong>2. Luật sư tư vấn</strong></p>\r\n\r\n<p><strong>2.1.&nbsp;</strong><strong>Điều kiện cấp giấy ph&eacute;p cho thu&ecirc; lại lao động tại Hải Ph&ograve;ng</strong></p>\r\n\r\n<p>a. Điều kiện về người đại diện của doanh nghiệp xin cấp giấy ph&eacute;p cho thu&ecirc; lại lao động tại Hải Ph&ograve;ng.</p>\r\n\r\n<p>Người đại diện theo ph&aacute;p luật của doanh nghiệp thực hiện hoạt động cho thu&ecirc; lại lao động phải bảo đảm điều kiện:</p>\r\n\r\n<ul>\r\n	<li>L&agrave; người quản l&yacute; doanh nghiệp.</li>\r\n	<li>Kh&ocirc;ng c&oacute; &aacute;n t&iacute;ch.</li>\r\n	<li>Đ&atilde; l&agrave;m việc trong lĩnh vực cho thu&ecirc; lai lao động hoặc cung ứng lao động từ đủ 03 năm trở l&ecirc;n trong thời hạn 05 năm liền kề trước khi đề nghị cấp giấy ph&eacute;p.</li>\r\n</ul>\r\n\r\n<p>b. Điều kiện Doanh nghiệp xin cấp giấy ph&eacute;p cho thu&ecirc; lại lao động tại Hải Ph&ograve;ng:<br />\r\n&ndash; Doanh nghiệp đ&atilde; thực hiện k&yacute; quỹ 2.000.000.000 đồng tại ng&acirc;n h&agrave;ng thương mại hoặc chi nh&aacute;nh ng&acirc;n h&agrave;ng nước ngo&agrave;i th&agrave;nh lập v&agrave; hoạt động hợp ph&aacute;p tại Việt Nam.<br />\r\n&ndash; Đăng k&yacute; kinh doanh c&oacute; đăng k&yacute; kinh doanh ng&agrave;nh nghề cho thu&ecirc; lại lao động.</p>\r\n\r\n<p><strong>2.2. Hồ sơ y&ecirc;u cầu cấp giấy ph&eacute;p cho thu&ecirc; lại lao động</strong><br />\r\n&ndash; &nbsp;Văn bản đề nghị cấp giấy ph&eacute;p của doanh nghiệp.<br />\r\n&ndash; &nbsp;Bản sao Giấy chứng nhận đăng k&yacute; doanh nghiệp theo quy định của ph&aacute;p luật.<br />\r\n&ndash; &nbsp;Bản l&yacute; lịch tự thuật của người đại diện theo ph&aacute;p luật của doanh nghiệp.<br />\r\n&ndash;&nbsp; Phiếu l&yacute; lịch tư ph&aacute;p số 1 theo quy định ph&aacute;p luật về l&yacute; lịch tư ph&aacute;p của người đại diện theo ph&aacute;p luật của doanh nghiệp. Trường hợp người đại diện theo ph&aacute;p luật l&agrave; người nước ngo&agrave;i kh&ocirc;ng thuộc đối tượng được cấp phiếu l&yacute; lịch tư ph&aacute;p của Việt Nam th&igrave; phiếu l&yacute; lịch tư ph&aacute;p số 1 được thay thế bằng phiếu l&yacute; lịch tư ph&aacute;p hoặc văn bản x&aacute;c nhận người lao động nước ngo&agrave;i kh&ocirc;ng phải l&agrave; người phạm tội hoặc bị truy cứu tr&aacute;ch nhiệm h&igrave;nh sự của nước ngo&agrave;i cấp.<br />\r\nC&aacute;c văn bản n&ecirc;u tr&ecirc;n được cấp trước ng&agrave;y nộp hồ sơ kh&ocirc;ng qu&aacute; 06 th&aacute;ng.<br />\r\n&ndash; &nbsp;Văn bản chứng minh thời gian đ&atilde; l&agrave;m việc của người đại diện theo ph&aacute;p luật.<br />\r\n&ndash; &nbsp;Giấy chứng nhận k&yacute; quỹ kinh doanh cho thu&ecirc; lại lao động</p>\r\n\r\n<p><strong>2.3. Tr&igrave;nh tự, thủ tục cấp giấy ph&eacute;p cho thu&ecirc; lại lao động</strong><br />\r\n&ndash; &nbsp;Doanh nghiệp gửi một bộ hồ sơ &nbsp;y&ecirc;u cầu cấp giấy ph&eacute;p cho thu&ecirc; lại lao động đến Sở Lao động &ndash; Thương binh v&agrave; X&atilde; hội&nbsp; th&agrave;nh phố Hải Ph&ograve;ng.</p>\r\n\r\n<ul>\r\n	<li>&nbsp;Hồ sơ đề nghị cấp giấy ph&eacute;p được tiếp nhận khi c&oacute; đủ c&aacute;c giấy tờ theo quy định. Sau khi tiếp nhận hồ sơ, Sở Lao động &ndash; Thương binh v&agrave; X&atilde; hội cấp giấy bi&ecirc;n nhận ghi r&otilde; ng&agrave;y, th&aacute;ng, năm nhận hồ sơ.</li>\r\n	<li>Trong thời hạn 20 ng&agrave;y l&agrave;m việc kể từ ng&agrave;y nhận được hồ sơ đảm bảo theo quy định, Sở Lao động &ndash; Thương binh v&agrave; X&atilde; hội thẩm tra, tr&igrave;nh Chủ tịch Ủy ban nh&acirc;n d&acirc;n cấp tỉnh cấp giấy ph&eacute;p hoạt động cho thu&ecirc; lại lao động đối với doanh nghiệp.</li>\r\n	<li>&nbsp;Trường hợp hồ sơ kh&ocirc;ng đảm bảo theo quy định, trong thời hạn 10 ng&agrave;y l&agrave;m việc kể từ ng&agrave;y tiếp nhận hồ sơ, Sở Lao động &ndash; Thương binh v&agrave; X&atilde; hội c&oacute; văn bản y&ecirc;u cầu doanh nghiệp ho&agrave;n thiện hồ sơ.</li>\r\n	<li>Trong thời hạn 07 ng&agrave;y l&agrave;m việc kể từ ng&agrave;y nhận được hồ sơ tr&igrave;nh của Sở Lao động &ndash; Thương binh v&agrave; X&atilde; hội, Chủ tịch Ủy ban nh&acirc;n d&acirc;n cấp tỉnh xem x&eacute;t, cấp giấy ph&eacute;p đối với doanh nghiệp; trường hợp kh&ocirc;ng cấp giấy ph&eacute;p th&igrave; c&oacute; văn bản trả lời doanh nghiệp trong đ&oacute; n&ecirc;u r&otilde; l&yacute; do kh&ocirc;ng cấp giấy ph&eacute;p.</li>\r\n</ul>\r\n\r\n<p><strong>2.4. Thời hạn giải quyết</strong>: trong thời hạn 20 ng&agrave;y l&agrave;m việc kể từ khi nhận được hồ sơ hợp lệ.</p>\r\n\r\n<p><strong>2.5.&nbsp;</strong>&nbsp;<strong>Thẩm quyền cấp giấy:</strong>&nbsp;Sở Lao động-Thương binh v&agrave; X&atilde; hội Hải Ph&ograve;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Dịch vụ cấp Giấy ph&eacute;p cho thu&ecirc; lại lao động tại Hải Ph&ograve;ng của CMA</strong>:</p>\r\n\r\n<ul>\r\n	<li>Tư vấn miễn ph&iacute; c&aacute;c vấn đề, hỏi đ&aacute;p ph&aacute;p l&yacute; li&ecirc;n quan tới việc Cấp giấy ph&eacute;p cho thu&ecirc; lại lao động tại Hải Ph&ograve;ng.</li>\r\n	<li>Soạn thảo, hỗ trợ chuẩn bị hồ sơ đ&aacute;p ứng c&aacute;c y&ecirc;u cầu của cơ quan chức năng li&ecirc;n quan tới Cấp lại giấy ph&eacute;p cho thu&ecirc; lại lao động tại Hải Ph&ograve;ng</li>\r\n	<li>Đại diện nộp hồ sơ, bổ sung hồ sơ xin Cấp giấy ph&eacute;p cho thu&ecirc; lại lao động tại Hải Ph&ograve;ng theo y&ecirc;u cầu của cơ quan chức năng.</li>\r\n	<li>Nhận kết quả v&agrave; trả kết quả Cấp giấy ph&eacute;p cho thu&ecirc; lại lao động tại Hải Ph&ograve;ng trực tiếp cho kh&aacute;ch h&agrave;ng.</li>\r\n	<li><strong>Thời hạn giải quyết:</strong>&nbsp;trong thời hạn 20 ng&agrave;y l&agrave;m việc kể từ khi nhận được hồ sơ hợp lệ.</li>\r\n</ul>\r\n', 1);

