--
-- Cấu trúc bảng cho bảng `dichvusodo`
--

CREATE TABLE `dichvusodo` (
  `id` int(11) NOT NULL,
  `demuc` varchar(500) NOT NULL,
  `tieude` varchar(500) NOT NULL,
  `anh` text NOT NULL,
  `thoigiandang` datetime NOT NULL DEFAULT current_timestamp(),
  `noidung` text NOT NULL,
  `taikhoanluatsu_id` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `dichvusodo`
--

INSERT INTO `dichvusodo` (`id`, `demuc`, `tieude`, `anh`, `thoigiandang`, `noidung`, `taikhoanluatsu_id`) VALUES
(1, 'Sang tên sổ đỏ', 'Người làm thủ tục cấp giấy chứng nhận chết trước khi được cấp', 'thua-ke-doi-voi-dat-chua-duoc-cap-so-do-theo-quy-dinh-moi.jpg', '2023-03-13 10:15:57', '<p><span style=\"font-size:14px\">Vừa qua, C&ocirc;ng ty Luật CMA nhận được c&acirc;u hỏi của kh&aacute;ch h&agrave;ng c&oacute; địa chỉ mail &ldquo;nguyen&hellip;@gmail.com&rdquo; với nội dung: Năm 2018, bố t&ocirc;i l&agrave;m thủ tục cấp Giấy chứng nhận quyền sử dụng đất lần đầu tại Văn ph&ograve;ng đăng k&yacute; đất đai UBND quận NQ (VPDKĐĐ). Trong qu&aacute; tr&igrave;nh đang thực hiện thủ tục, bố t&ocirc;i kh&ocirc;ng may l&acirc;m bệnh đột ngột qua đời, kh&ocirc;ng để lại di ch&uacute;c. Đến ng&agrave;y hẹn th&ocirc;ng b&aacute;o trả kết quả, t&ocirc;i ra Văn ph&ograve;ng đăng k&yacute; đất đai, tr&igrave;nh b&agrave;y về việc bố t&ocirc;i đ&atilde; mất v&agrave; xin hướng dẫn thủ tục để nhận sổ đỏ, th&igrave; được c&aacute;n bộ ở đ&acirc;y y&ecirc;u cầu gia đ&igrave;nh t&ocirc;i phải l&agrave;m thủ tục cử người đại diện để nhận sổ đỏ. Tất cả t&agrave;i sản, mọi người trong gia đ&igrave;nh đều đồng &yacute; để lại cho t&ocirc;i, kh&ocirc;ng c&oacute; bất cứ tranh chấp g&igrave;. Vậy, gia đ&igrave;nh t&ocirc;i phải l&agrave;m những g&igrave; để nhận lại sổ đỏ?</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:14px\"><strong>Luật sư CMA tư vấn</strong></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:14px\">Căn cứ khoản 4 Điều 79&nbsp;<a href=\"https://luatannam.vn/?s=ngh%E1%BB%8B+%C4%91%E1%BB%8Bnh+43%2F2014\">Nghị&nbsp;định 43/2014/NĐ-CP</a>:</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>&ldquo;Trường hợp người đề nghị cấp Giấy chứng nhận đ&atilde; chết trước khi được trao Giấy chứng nhận th&igrave; người được thừa kế quyền sử dụng đất theo quy định của ph&aacute;p luật thừa kế nộp bổ sung giấy tờ về thừa kế theo quy định.</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>Văn ph&ograve;ng đăng k&yacute; đất đai x&aacute;c nhận thừa kế v&agrave;o Giấy chứng nhận đ&atilde; k&yacute; hoặc lập hồ sơ để tr&igrave;nh cơ quan c&oacute; thẩm quyền cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất cho người được thừa kế theo quy định của Bộ T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường. Người được thừa kế c&oacute; tr&aacute;ch nhiệm thực hiện nghĩa vụ t&agrave;i ch&iacute;nh về quyền sử dụng đất, t&agrave;i sản gắn liền với đất trong trường hợp người được cấp Giấy chứng nhận đ&atilde; chết phải thực hiện theo quy định của ph&aacute;p luật.&rdquo;</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bố bạn mất trước khi nhận Giấy chứng nhận quyền sử dụng đất th&igrave; bạn phải nộp bổ sung c&aacute;c&nbsp;<strong>giấy tờ về thừa kế</strong>&nbsp;theo quy định tại VPĐKĐĐ quận NQ v&agrave; thực hiện c&aacute;c nghĩa vụ t&agrave;i ch&iacute;nh (nếu c&oacute;) như: lệ ph&iacute; trước bạ, tiền sử dụng đất, thuế đất h&agrave;ng năm c&ograve;n thiếu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Hồ sơ, giấy tờ về nhận thừa kế cần chuẩn bị:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14px\">Bản sao CMND, CCCD, hộ khẩu, giấy chứng tử của người thừa kế thuộc h&agrave;ng thừa kế thứ nhất,</span></li>\r\n</ul>\r\n\r\n<h6><span style=\"font-size:14px\">&middot;&nbsp; Giấy tờ chứng minh quan hệ giữa người để lại di sản v&agrave; người được thừa kế: Giấy khai sinh, đăng k&yacute; kết h&ocirc;n</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&middot;&nbsp; Sơ yếu l&yacute; lịch của người được nhận di sản thừa kế.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&middot;&nbsp; Giấy chứng tử của người chết.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Sau khi kiểm tra hồ sơ thấy đầy đủ, ph&ugrave; hợp quy định của ph&aacute;p luật, cơ quan c&ocirc;ng chứng tiến h&agrave;nh ni&ecirc;m yết c&ocirc;ng khai tại trụ sở UBND x&atilde; nơi thường tr&uacute; cuối c&ugrave;ng của bố bạn v&agrave; nơi c&oacute; bất động sản. Trong v&ograve;ng 15 ng&agrave;y ni&ecirc;m yết, kh&ocirc;ng c&oacute; khiếu nại, tố c&aacute;o g&igrave; th&igrave; cơ quan c&ocirc;ng chứng lập v&agrave; chứng nhận&nbsp;<strong>văn bản&nbsp;</strong><strong>cử người đại diện đề nghị đứng t&ecirc;n tr&ecirc;n Giấy chứng nhận</strong>.</span></h6>\r\n\r\n<p><span style=\"font-size:14px\">Sau khi nộp đầy đủ c&aacute;c giấy tờ tr&ecirc;n v&agrave; thực hiện đủ nghĩa vụ t&agrave;i ch&iacute;nh, VPĐKĐĐ sẽ x&aacute;c nhận thừa kế v&agrave;o Giấy chứng nhận đ&atilde; k&yacute;. Nếu chưa cấp Giấy chứng nhận, VPĐKĐĐ sẽ bổ sung hồ sơ đề nghị UBND quận NQ sẽ cấp giấy chứng nhận quyền sử dụng đất cho bạn.</span></p>\r\n', 1),
(2, 'Cấp sổ đỏ lần đầu', 'Cấp sổ đỏ nhà đất không có giấy tờ có vi phạm tại thời điểm bắt đầu sử dụng', '1052471_72077.jpg', '2023-03-13 10:36:34', '<ol>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\"><strong>Cơ sở ph&aacute;p l&yacute;:&nbsp;</strong></span></h6>\r\n	</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Điều 101 Luật đất đai năm 2013;</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Điều 22 NĐ 43/2014 quy định cấp Sổ Đỏ (Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất);</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Điều 6, 7 Nghị định 45/2014/ND-CP quy định về thu tiền sử dụng đất.</span></h6>\r\n	</li>\r\n</ul>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>2. Lu&acirc;̣t sư CMA tư v&acirc;́n:</strong></span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Sử dụng đ&acirc;́t kh&ocirc;ng có gi&acirc;́y tờ được hi&ecirc;̉u là hi&ecirc;̣n trạng người sử dụng đ&acirc;́t kh&ocirc;ng có các gi&acirc;́y tờ v&ecirc;̀ quy&ecirc;̀n được sử dụng được quy định tại đi&ecirc;̀u 100 Lu&acirc;̣t đ&acirc;́t đai&nbsp;<em>(Tham khảo th&ecirc;m bài vi&ecirc;́t:&nbsp;<a href=\"https://tuvanluathaiphong.com/giay-to-chung-de-duoc-cap-so-do/\">Gi&acirc;́y tờ chứng minh đ&ecirc;̉ được c&acirc;́p s&ocirc;̉ đỏ</a>)</em></span></h6>\r\n\r\n<p><span style=\"font-size:14px\">Đối với trường hợp người sử dụng đất sử dụng đất kh&ocirc;ng c&oacute; giấy tờ v&agrave; tại thời điểm sử dụng đất c&oacute; một trong c&aacute;c h&agrave;nh vi vi phạm sau đ&acirc;y th&igrave; được xem x&eacute;t cấp giấy chứng nhận quyền sử dụng đất quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất (Sổ đỏ, sổ hồng), cụ thể:</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>2.1. C&aacute;c trường hợp được xem x&eacute;t cấp Sổ đỏ</strong>:</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>a)</strong>&nbsp;<strong>Lấn, chiếm h&agrave;nh lang bảo vệ an to&agrave;n c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng</strong>&nbsp;sau khi Nh&agrave; nước đ&atilde; c&ocirc;ng bố, cắm mốc h&agrave;nh lang bảo vệ hoặc lấn, chiếm l&ograve;ng đường, lề đường, vỉa h&egrave; sau khi Nh&agrave; nước đ&atilde; c&ocirc;ng bố chỉ giới x&acirc;y dựng hoặc lấn, chiếm đất sử dụng cho mục đ&iacute;ch x&acirc;y dựng trụ sở cơ quan, c&ocirc;ng tr&igrave;nh sự nghiệp, c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng kh&aacute;c th&igrave; Nh&agrave; nước thu hồi đất để trả lại cho c&ocirc;ng tr&igrave;nh m&agrave; kh&ocirc;ng cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất đối với diện t&iacute;ch đất đ&atilde; lấn, chiếm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trường hợp đ&atilde; c&oacute; điều chỉnh quy hoạch sử dụng đất, quy hoạch x&acirc;y dựng được cơ quan nh&agrave; nước c&oacute; thẩm quyền ph&ecirc; duyệt m&agrave; nay diện t&iacute;ch đất lấn, chiếm kh&ocirc;ng c&ograve;n thuộc h&agrave;nh lang bảo vệ an to&agrave;n c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng; kh&ocirc;ng thuộc chỉ giới x&acirc;y dựng đường giao th&ocirc;ng; kh&ocirc;ng c&oacute; mục đ&iacute;ch sử dụng cho trụ sở cơ quan, c&ocirc;ng tr&igrave;nh sự nghiệp v&agrave; c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng kh&aacute;c th&igrave; người đang sử dụng đất được xem x&eacute;t cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất v&agrave; phải nộp tiền sử dụng đất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>b) Trường hợp đang sử dụng diện t&iacute;ch</strong>&nbsp;<strong>đất lấn, chiếm thuộc quy hoạch sử dụng đất cho mục đ&iacute;ch x&acirc;y dựng c&ocirc;ng tr&igrave;nh hạ tầng c&ocirc;ng cộng</strong>&nbsp;th&igrave; Ủy ban nh&acirc;n d&acirc;n cấp tỉnh chỉ đạo thu hồi đất đ&atilde; lấn, chiếm để giao đất cho chủ đầu tư khi triển khai x&acirc;y dựng c&ocirc;ng tr&igrave;nh đ&oacute;. Người đang sử dụng đất vi phạm được tạm thời sử dụng cho đến khi Nh&agrave; nước thu hồi đất nhưng phải giữ nguy&ecirc;n hiện trạng sử dụng đất v&agrave; phải k&ecirc; khai đăng k&yacute; đất đai theo quy định</span></p>\r\n\r\n<p><span style=\"font-size:14px\">c) Trường hợp<strong>&nbsp;lấn, chiếm đất v&agrave; nay đang sử dụng v&agrave;o mục đ&iacute;ch sản xuất n&ocirc;ng nghiệp</strong>&nbsp;hoặc l&agrave;m<strong>&nbsp;nh&agrave; ở</strong>&nbsp;v&agrave; kh&ocirc;ng thuộc quy hoạch bảo vệ v&agrave; ph&aacute;t triển rừng đặc dụng, rừng ph&ograve;ng hộ, x&acirc;y dựng c&ocirc;ng tr&igrave;nh hạ tầng c&ocirc;ng cộng th&igrave; người đang sử dụng đất được xem x&eacute;t cấp Giấy chứng nhận quyền sử dụng đất, quyền sở hữu nh&agrave; ở v&agrave; t&agrave;i sản kh&aacute;c gắn liền với đất. Trường hợp lấn, chiếm đất kể từ ng&agrave;y 01 th&aacute;ng 7 năm 2004 đến trước ng&agrave;y 01 th&aacute;ng 7 năm 2014, đang sử dụng v&agrave;o mục đ&iacute;ch sản xuất n&ocirc;ng nghiệp v&agrave; hiện nay diện t&iacute;ch đất n&agrave;y vẫn được x&aacute;c định giao cho n&ocirc;ng trường, l&acirc;m trường quản l&yacute;, sử dụng th&igrave; Ủy ban nh&acirc;n d&acirc;n cấp tỉnh thu hồi đất lấn, chiếm để trả lại cho n&ocirc;ng trường, l&acirc;m trường.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">d) Trường hợp&nbsp;<strong>lấn, chiếm đất chưa sử dụng</strong>&nbsp;hoặc&nbsp;<strong>tự &yacute; chuyển mục đ&iacute;ch sử dụng đất</strong>&nbsp;thuộc trường hợp phải xin ph&eacute;p theo quy định của ph&aacute;p luật về đất đai m&agrave; chưa được cơ quan nh&agrave; nước c&oacute; thẩm quyền cho ph&eacute;p:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trường hợp thuộc dự &aacute;n nh&agrave; nước thu hồi đất th&igrave; Người đang sử dụng đất vi phạm được tạm thời sử dụng cho đến khi Nh&agrave; nước thu hồi đất, nhưng phải giữ nguy&ecirc;n hiện trạng đang sử dụng đất v&agrave; phải k&ecirc; khai đăng k&yacute; đất đai theo quy định;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trường hợp đất kh&ocirc;ng thuộc dự &aacute;n nh&agrave; nước thu hồi đất th&igrave; người đang sử dụng đất được xem x&eacute;t cấp Sổ đỏ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">e) Hộ gia đ&igrave;nh, c&aacute; nh&acirc;n đang sử dụng đất n&ocirc;ng nghiệp do tự&nbsp;<strong>khai hoang</strong>&nbsp;m&agrave; đất đ&oacute; ph&ugrave; hợp với quy hoạch sử dụng đất đ&atilde; được cơ quan nh&agrave; nước c&oacute; thẩm quyền duyệt, kh&ocirc;ng c&oacute; tranh chấp th&igrave; được Nh&agrave; nước c&ocirc;ng nhận quyền sử dụng đất theo hạn mức do Ủy ban nh&acirc;n d&acirc;n cấp tỉnh quy định; nếu vượt hạn mức do Ủy ban nh&acirc;n d&acirc;n cấp tỉnh quy định th&igrave; diện t&iacute;ch vượt hạn mức phải chuyển sang thu&ecirc;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>2.2. Hạn mưc được c&ocirc;ng nhận, cấp Sổ đỏ</strong>&nbsp;(Tham khảo b&agrave;i viết:&nbsp;<a href=\"https://tuvanluathaiphong.com/cap-so-do-nha-dat-khong-co-giay-to-khong-vi-pham/\">Cấp sổ đỏ nh&agrave; đất kh&ocirc;ng c&oacute; giấy tờ kh&ocirc;ng vi phạm</a>)</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>2.3. Nghĩa vụ t&agrave;i ch&iacute;nh phải thực hiện khi được nh&agrave; nước cấp Sổ đỏ:</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">(1) Nếu sử dụng đất trước ng&agrave;y 15/10/1993 th&igrave; phải: i) Với đất ở: Nộp 50% tiền sử dụng đất với phần diện t&iacute;ch đất trong hạn mức, 100% tiền sử dụng đất với phần diện t&iacute;ch đất ngo&agrave;i hạn mức; ii) Với đất sản xuất kinh doanh phi n&ocirc;ng nghiệp th&igrave; phải nộp 50% tiền sử dụng đất; v&agrave;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">(2) Nếu sử dụng đất trong giai đoạn từ 15/10/1993 đến trước 1/7/2014 th&igrave; phải: i) Với đất ở: Nộp 100% tiền sử dụng đất; ii) Với đất sản xuất kinh doanh phi n&ocirc;ng nghiệp th&igrave; phải nộp 100% tiền sử dụng đất.</span></p>\r\n', 1);

