--
-- Cấu trúc bảng cho bảng `luatsutuvan`
--

CREATE TABLE `luatsutuvan` (
  `id` int(11) NOT NULL,
  `demuc` varchar(500) NOT NULL,
  `tieude` varchar(500) NOT NULL,
  `anh` text NOT NULL,
  `thoigiandang` datetime NOT NULL DEFAULT current_timestamp(),
  `noidung` text NOT NULL,
  `taikhoanluatsu_id` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `luatsutuvan`
--

INSERT INTO `luatsutuvan` (`id`, `demuc`, `tieude`, `anh`, `thoigiandang`, `noidung`, `taikhoanluatsu_id`) VALUES
(6, 'Luật sư tư vấn Hình sự', 'Nguyên tắc xử lý trách nhiệm hình sự người chưa thành niên phạm tội', 'download.png', '2023-03-13 09:58:30', '<h6><span style=\"font-size:14px\"><strong>Người chưa th&agrave;nh ni&ecirc;n phạm tội l&agrave; người từ đủ 14 tuổi đến dưới 18 tuổi thực hiện h&agrave;nh vi nguy hiểm cho x&atilde; hội được quy định trong Bộ luật h&igrave;nh sự l&agrave; tội phạm.&nbsp;</strong></span></h6>\r\n\r\n<p><span style=\"font-size:14px\">Xuất ph&aacute;t từ t&iacute;nh chất đặc biệt của chủ thể h&agrave;nh vi phạm tội, l&agrave; người được đ&aacute;nh gi&aacute; chung l&agrave; c&oacute; sự hiểu biết c&ograve;n hạn chế về t&iacute;nh chất, mức độ nguy hiểm của h&agrave;nh vi. V&igrave; vậy, Bộ luật h&igrave;nh sự quy định bổ sung, kh&aacute;c so với người th&agrave;nh ni&ecirc;n phạm tội về: Nguy&ecirc;n tắc xử l&yacute;, &aacute;p dụng h&igrave;nh phạt.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>1.&nbsp; Về nguy&ecirc;n tắc xử l&yacute; người chưa th&agrave;nh ni&ecirc;n phạm tội:&nbsp;</strong>Căn cứ điều 90, 91 Bộ luật h&igrave;nh sự, th&igrave; nguy&ecirc;n tắc xử l&yacute; người dưới 18 tuổi phạm tội bao gồm:</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>a) Việc xử l&yacute; v&igrave; lợi &iacute;ch tốt nhấ</strong>t của người dưới 18 tuổi, nhằm mục đ&iacute;ch gi&aacute;o dục, gi&uacute;p đỡ họ sữa chưa sai lầm, ph&aacute;t triển l&agrave;nh mạnh trở th&agrave;nh c&ocirc;ng d&acirc;n c&oacute; &iacute;ch cho x&atilde; hội, kh&ocirc;ng nhằm mục đ&iacute;ch trừng phạt như người từ đủ 18 tuổi.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>b) C&oacute; thể miễn tr&aacute;ch nhiệm h&igrave;nh sự</strong>&nbsp;khi kh&ocirc;ng đ&aacute;p ứng điều kiện được miễn tr&aacute;ch nhiệm quy định tại điều 29 Bộ luật h&igrave;nh sự, nhưng c&oacute; nhiều t&igrave;nh tiết giảm nhẹ, tự nguyện khắc phục phần lớn hậu quả v&agrave; khi đ&aacute;p ứng c&aacute;c điều kiện sau:&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ndash;&nbsp; Đối với người từ đủ 16 tuổi đến dưới 18 tuổi: Tội phạm thực hiện thuộc loại tội phạm &iacute;t nghi&ecirc;m trọng (c&oacute; khung h&igrave;nh phạt đến 3 năm), phạm tội nghi&ecirc;m trọng (c&oacute; khung h&igrave;nh phạt đến 7 năm), trừ tội phạm được quy định tại c&aacute;c điều: Điều 134; Điều 141; Điều 171; Điều 248; Điều 249; Điều 250; Điều 251; Điều 252.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ndash;&nbsp; Đối với người từ đủ 14 tuổi đến dưới 16 tuổi: Tội phạm thực hiện l&agrave; tội rất nghi&ecirc;m trọng (c&oacute; khung h&igrave;nh phạt đến 15 năm) quy định tại khoản 2 Điều 12 của Bộ luật h&igrave;nh sự, trừ c&aacute;c tội quy định tại c&aacute;c điều 123; c&aacute;c khoản 4, 5 v&agrave; khoản 6 Điều 134; Điều 141; Điều 142; Điều 150; Điều 151; Điều 171; Điều 248; Điều 249; Điều 250; Điều 251; Điều 252.&nbsp;</span></p>\r\n\r\n<h6><span style=\"font-size:14px\">Người chưa th&agrave;nh ni&ecirc;n được miễn tr&aacute;ch nhiệm h&igrave;nh sự phải bị &aacute;p dụng một trong c&aacute;c biện ph&aacute;p gi&aacute;m s&aacute;t, gi&aacute;o dục như: Khiển tr&aacute;ch, h&ograve;a giải tại cộng đồng, gi&aacute;o dục tại x&atilde;, phường, thị trấn.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>c) Chỉ &aacute;p dụng h&igrave;nh phạt khi c&aacute;c biện ph&aacute;p</strong>&nbsp;&ldquo;biện ph&aacute;p Gi&aacute;m s&aacute;t, gi&aacute;o dục&rdquo; (Khiển tr&aacute;ch, H&ograve;a giải tại cộng đồng, Gi&aacute;o dục tại x&atilde;, phường, thị trấn) hoặc việc &aacute;p dụng &ldquo;biện ph&aacute;p Gi&aacute;o dục tại trường gi&aacute;o dưỡng&rdquo; gi&aacute;o dục, ph&ograve;ng ngừa kh&ocirc;ng hiệu quả.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>d) Kh&ocirc;ng xử phạt t&ugrave; chung th&acirc;n hoặc tử h&igrave;nh</strong>.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>e) Hạn chế &aacute;p dụng h&igrave;nh phạt t&ugrave;</strong>:T&ograve;a &aacute;n chỉ &aacute;p dụng h&igrave;nh phạt t&ugrave; c&oacute; thời hạn đối với người dưới 18 tuổi phạm tội khi x&eacute;t thấy c&aacute;c h&igrave;nh phạt v&agrave; biện ph&aacute;p gi&aacute;o dục kh&aacute;c kh&ocirc;ng c&oacute; t&aacute;c dụng răn đe, ph&ograve;ng ngừa.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>f) Mức &aacute;n tuy&ecirc;n cho người dưới 18 tuổi nhẹ hơn</strong>&nbsp;mức &aacute;n &aacute;p dụng đối với người đủ 18 tuổi trở l&ecirc;n phạm tội tương ứng v&agrave; với thời hạn th&iacute;ch hợp ngắn nhất.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash; Đối với người từ đủ 16 đến dưới 18 tuổi: Nếu điều luật được &aacute;p dụng quy định h&igrave;nh phạt chung th&acirc;n, tử h&igrave;nh th&igrave; mức h&igrave;nh phạt cao nhất được &aacute;p dụng kh&ocirc;ng qu&aacute; 18 năm. Nếu l&agrave; t&ugrave; c&oacute; thời hạn th&igrave; mức h&igrave;nh phạt cao nhất kh&ocirc;ng qu&aacute; 3/4.</span></h6>\r\n\r\n<p><span style=\"font-size:14px\">&ndash; Đối với người từ đủ 14 đến dưới 16: Nếu điều luật được &aacute;p dụng quy định h&igrave;nh phạt chung th&acirc;n, tử h&igrave;nh th&igrave; mức h&igrave;nh phạt cao nhất được &aacute;p dụng kh&ocirc;ng qu&aacute; 12 năm. Nếu l&agrave; t&ugrave; c&oacute; thời hạn th&igrave; mức h&igrave;nh phạt cao nhất kh&ocirc;ng qu&aacute; 1/2.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ndash; Đối với trường hợp chuẩn bị phạm tội: (1) Người từ đủ 14 đến dưới 16 mức h&igrave;nh phạt kh&ocirc;ng qu&aacute; 1/3; (2) Người từ đủ 16 đến dưới 18 mức h&igrave;nh phạt kh&ocirc;ng qu&aacute; 1/2.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ndash; Đối với trường hợp phạm tội chưa đạt: (1) Người từ đủ 14 đến dưới 16 mức h&igrave;nh phạt kh&ocirc;ng qu&aacute; 1/3; (2) Người từ đủ 16 đến dưới 18 mức h&igrave;nh phạt kh&ocirc;ng qu&aacute; 1/2&nbsp; mức h&igrave;nh phạt đ&atilde; được xem x&eacute;t &aacute;p dụng theo quy định tại điều 99, 100, 101 Bộ luật h&igrave;nh sự.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ndash; Đối với trường hợp người chưa th&agrave;nh ni&ecirc;n phạm nhiều tội: (1) Nếu h&igrave;nh phạt chung l&agrave; cải tạo kh&ocirc;ng giam giữ th&igrave; mức phạt cao nhất kh&ocirc;ng qu&aacute; 03 năm; (2) Nếu h&igrave;nh phạt chung l&agrave; t&ugrave; c&oacute; thời hạn th&igrave; mức phạt cao nhất kh&ocirc;ng qu&aacute; 18 năm đối với người từ 16 đến dưới 18, kh&ocirc;ng qu&aacute; 12 năm đối với người từ đủ 14 đến dưới 16.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ndash; Đối với trường hợp người chưa th&agrave;nh ni&ecirc;n phạm tội c&oacute; tội thực hiện trước khi đủ 16 tuổi, c&oacute; tội thực hiện sau khi đủ 16 tuổi: (1) Nếu mức h&igrave;nh phạt đ&atilde; tuy&ecirc;n đối với tội được thực hiện trước khi người đ&oacute; đủ 16 tuổi nặng hơn hoặc bằng mức h&igrave;nh phạt đ&atilde; tuy&ecirc;n đối với tội được thực hiện sau khi đủ 16 tuổi th&igrave; h&igrave;nh phạt chung kh&ocirc;ng vượt qu&aacute; 12 năm; (2) Nếu mức h&igrave;nh phạt đ&atilde; tuy&ecirc;n đối với tội được thực hiện sau khi người đ&oacute; đủ 16 tuổi nặng mức h&igrave;nh phạt đ&atilde; tuy&ecirc;n đối với tội được thực hiện trước khi đủ 16 tuổi th&igrave; h&igrave;nh phạt chung kh&ocirc;ng vượt qu&aacute; 18 năm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&ndash; Đối với trường hợp phạm nhiều tội, c&oacute; tội trước khi 18 tuổi, c&oacute; tuổi sau 18 tuổi: (1) Nếu h&igrave;nh phạt đối với tội được thực hiện trước khi 18 tuổi nặng hơn hoặc bằng mức h&igrave;nh phạt đối với mức h&igrave;nh phạt của tội thực hiện sau khi 18 tuổi th&igrave; h&igrave;nh phạt chung kh&ocirc;ng qu&aacute; 18 năm đối với người từ 16 đến dưới 18, kh&ocirc;ng qu&aacute; 12 năm đối với người từ 14 đến dưới 16; (2) Nếu mức h&igrave;nh phạt của tội thực hiện sau khi 18 tuổi nặng hơn mức h&igrave;nh phạt đối với tội thực hiện trước khi 18 tuổi th&igrave; h&igrave;nh phạt được tổng hợp chung như đối với người từ đủ 18 tuổi.</span></p>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>g) Kh&ocirc;ng &aacute;p dụng h&igrave;nh phạt tiền</strong>&nbsp;với người chưa th&agrave;nh ni&ecirc;n phạm tội ở độ tuổi từ đủ 14 đến dưới 16 tuổi. Đối với người từ đủ 16 đến dưới 18 tuổi th&igrave; phạt tiền được &aacute;p dụng l&agrave; h&igrave;nh phạt ch&iacute;nh trong trường hợp người đ&oacute; c&oacute; t&agrave;i sản ri&ecirc;ng, mức phạt tiền kh&ocirc;ng qu&aacute; 1/2 mức điều luật quy định.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>h) Kh&ocirc;ng &aacute;p dụng h&igrave;nh phạt bổ sung</strong>&nbsp;đối với người dưới 18 tuổi phạm tội.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>i) &Aacute;n đ&atilde; tuy&ecirc;n</strong>&nbsp;với người chưa th&agrave;nh ni&ecirc;n phạm tội khi chưa đủ 16 tuổi&nbsp;<strong>kh&ocirc;ng được t&iacute;nh l&agrave; t&aacute;i phạm hay t&aacute;i phạm nguy hiểm.</strong></span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>2. C&aacute;c h&igrave;nh phạt &aacute;p dụng đối với người chưa th&agrave;nh ni&ecirc;n:</strong></span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>a) Cảnh c&aacute;o (k1 điều 98 Bộ luật h&igrave;nh sự):</strong></span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash;&nbsp;<strong>T&iacute;nh chất</strong>: L&agrave; loại gi&aacute;o dục s&acirc;u sắc, g&acirc;y ảnh hưởng lớn tới tinh thần.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash; Điều kiện &aacute;p dụng: Phạm tội thuộc loại &iacute;t nghi&ecirc;m trọng, c&oacute; nhiều t&igrave;nh tiết giảm nhẹ tại Điều 51 Bộ luật h&igrave;nh sự.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>b) Phạt tiền (điều 99 Bộ luật h&igrave;nh sự)</strong></span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash; Điều kiện &aacute;p dụng: Người chưa th&agrave;nh ni&ecirc;n c&oacute; độ tuổi từ đủ 16 đến dưới 18 tuổi, c&oacute; thu nhập hoặc c&oacute; t&agrave;i sản ri&ecirc;ng.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash; Căn cứ v&agrave;o t&igrave;nh chất, mức độ nghi&ecirc;m trọng của tội phạm, t&igrave;nh h&igrave;nh t&agrave;i ch&iacute;nh của người chưa th&agrave;nh ni&ecirc;n, sự biến động của gi&aacute; cả.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash; Mức phạt: kh&ocirc;ng qu&aacute; 1/2 mức phạt Bộ luật h&igrave;nh sự quy định.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>c) Cải tạo kh&ocirc;ng giam giữ&nbsp;(điều 100 Bộ luật h&igrave;nh sự)</strong></span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash; Điều kiện &aacute;p dụng: (1) Người chưa th&agrave;nh ni&ecirc;n c&oacute; độ tu&ocirc;i từ đủ 16 đến dưới 18 tuổi phạm tội &iacute;t nghi&ecirc;m trọng, nghi&ecirc;m trọng; hoặc phạm tội rất nghi&ecirc;m trọng do v&ocirc; &yacute;; hoặc (2) Người từ đủ 14 -dưới 16 tuổi phạm tội rất nghi&ecirc;m trọng.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash; Kh&ocirc;ng bị khấu trừ thu nhập (nếu c&oacute;).</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash; Thời hạn: Kh&ocirc;ng qu&aacute; 1/2 thời hạn m&agrave; điều luật quy định.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>d) T&ugrave; c&oacute; thời hạn&nbsp;(điều 101 Bộ luật h&igrave;nh sự)</strong></span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash; Điều kiện &aacute;p dụng: Phạm tội rất nghi&ecirc;m trọng hoặc đặc biệt nghi&ecirc;m trọng, kh&ocirc;ng đủ điều kiện &aacute;p dụng h&igrave;nh phạt kh&aacute;c nhẹ hơn.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash; Mức h&igrave;nh phạt đối với người từ đủ 16 &ndash; dưới 18 tuổi:</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Nếu điều luật quy định h&igrave;nh phạt cao nhất l&agrave; chung th&acirc;n hoặc tử h&igrave;nh th&igrave; phạt t&ugrave; kh&ocirc;ng qu&aacute; 18 năm t&ugrave;.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Nếu điều luật quy định h&igrave;nh phạt cao nhất l&agrave; t&ugrave; c&oacute; thời hạn th&igrave; phạt t&ugrave; kh&ocirc;ng qu&aacute; 3/4 mức phạt.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash;&nbsp; Mức h&igrave;nh phạt &aacute;p dụng với người từ đủ 14 tuổi đến dưới 16 tuổi:</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Nếu điều luật quy định h&igrave;nh phạt cao nhất l&agrave; chung th&acirc;n hoặc tử h&igrave;nh th&igrave; phạt t&ugrave; kh&ocirc;ng qu&aacute; 12 năm.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Nếu điều luật quy định h&igrave;nh phạt cao nhất l&agrave; t&ugrave; c&oacute; thời hạn th&igrave; phạt t&ugrave; kh&ocirc;ng qu&aacute; 1/2 số năm phạt t&ugrave;.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>3. X&oacute;a &aacute;n t&iacute;ch:</strong></span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash; Người dưới 18 tuổi bị kết &aacute;n được coi l&agrave; kh&ocirc;ng c&oacute; &aacute;n t&iacute;ch, nếu thuộc một trong c&aacute;c trường hợp sau đ&acirc;y:</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">(1) Người từ đủ 14 đến dưới 16 tuổi;</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">(2) Người từ đủ 16 tuổi đến dưới 18 tuổi bị kết &aacute;n về tội phạm &iacute;t nghi&ecirc;m trọng, tội phạm nghi&ecirc;m trọng hoặc tội phạm rất nghi&ecirc;m trọng do v&ocirc; &yacute;;</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">(3) Người bị &aacute;p dụng biện ph&aacute;p tư ph&aacute;p</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">&ndash; Người từ đủ 16 đến dưới 18 tuổi bị kết &aacute;n về tội phạm rất nghi&ecirc;m trọng do cố &yacute; hoặc tội phạm đặc biệt nghi&ecirc;m trọng th&igrave;&nbsp;đương nhi&ecirc;n x&oacute;a &aacute;n t&iacute;ch&nbsp;nếu trong thời hạn 03 năm t&iacute;nh từ khi chấp h&agrave;nh xong h&igrave;nh phạt ch&iacute;nh hoặc từ khi hết thời hiệu thi h&agrave;nh bản &aacute;n m&agrave; người đ&oacute; kh&ocirc;ng thực hiện h&agrave;nh vi phạm tội mới.</span></h6>\r\n', 1),
(7, 'Luật sư tư vấn Dân sự', 'Thu hồi Sổ hộ khẩu những điều cần biết', 'hoi-dap-phap-luat-2.jpg', '2023-03-13 10:24:02', '<p>Theo quy định tại&nbsp;<a href=\"https://thuvienphapluat.vn/van-ban/Quyen-dan-su/Luat-68-2020-QH14-cu-tru-435315.aspx\">Luật Cư tr&uacute; 2020</a>&nbsp;th&igrave; từ ng&agrave;y 01/01/2023, to&agrave;n bộ sổ hộ khẩu, sổ tạm tr&uacute; bằng giấy sẽ kh&ocirc;ng c&ograve;n gi&aacute; trị sử dụng. Do đ&oacute;, sổ hộ khẩu, sổ tạm tr&uacute; đ&atilde; cấp chỉ c&ograve;n được sử dụng đến hết ng&agrave;y 31/12/2022. Sổ hộ khẩu được xem l&agrave; giấy tờ quan trọng, bắt buộc phải c&oacute; trong một số giao dịch, thủ tục h&agrave;nh ch&iacute;nh. Vậy, khi thu hồi Sổ hộ khẩu giấy, người d&acirc;n cần lưu &yacute; một số vấn đề sau:</p>\r\n\r\n<ol>\r\n	<li><strong>Khi n&agrave;o thu hồi Sổ hộ khẩu giấy ?</strong></li>\r\n</ol>\r\n\r\n<p>Theo&nbsp;<a href=\"https://thuvienphapluat.vn/van-ban/Quyen-dan-su/Thong-tu-55-2021-TT-BCA-huong-dan-Luat-Cu-tru-466836.aspx\">Th&ocirc;ng tư 55/2021/TT-BCA</a>&nbsp;c&oacute; hiệu lực thi h&agrave;nh từ ng&agrave;y 01/7/2022 quy định: Cơ quan đăng k&yacute; cư tr&uacute; c&oacute; tr&aacute;ch nhiệm thu hồi Sổ hộ khẩu, Sổ tạm tr&uacute; đ&atilde; cấp, thực hiện điều chỉnh, cập nhật th&ocirc;ng tin trong Cơ sở dữ liệu về cư tr&uacute; theo quy định của Luật Cư tr&uacute; v&agrave; kh&ocirc;ng cấp mới, cấp lại Sổ hộ khẩu, Sổ tạm tr&uacute; khi c&ocirc;ng d&acirc;n thực hiện c&aacute;c thủ tục sau đ&acirc;y:</p>\r\n\r\n<p>&ndash; Đăng k&yacute; thường tr&uacute;;</p>\r\n\r\n<p>&ndash; Điều chỉnh th&ocirc;ng tin trong Cơ sở dữ liệu về cư tr&uacute;;</p>\r\n\r\n<p>&ndash; Thực hi&ecirc;n t&aacute;ch hộ;</p>\r\n\r\n<p>&ndash; X&oacute;a đăng k&yacute; thường tr&uacute;;</p>\r\n\r\n<p>&ndash; Đăng k&yacute; tạm tr&uacute;, gia hạn tạm tr&uacute;;</p>\r\n\r\n<p>&ndash; X&oacute;a đăng k&yacute; tạm tr&uacute; dẫn đến thay đổi th&ocirc;ng tin trong Sổ hộ khẩu, Sổ tạm tr&uacute;.</p>\r\n\r\n<ol start=\"2\">\r\n	<li><strong>Thu hồi Sổ hộ khẩu, d&ugrave;ng giấy tờ g&igrave; để thay thế?</strong></li>\r\n</ol>\r\n\r\n<p>Sổ hộ khẩu l&agrave; giấy tờ quan trọng sử dụng để chứng minh th&ocirc;ng tin cư tr&uacute; của người d&acirc;n, l&agrave; loại giấy tờ cần thiết để đi l&agrave;m nhiều thủ tục h&agrave;nh ch&iacute;nh, giao dịch quan trọng như: thủ tục đăng k&yacute; khai sinh, khai tử, đăng k&yacute; kết h&ocirc;n; thủ tục l&agrave;m sổ đỏ, chuyển nhượng quyền sử dụng đất; mua b&aacute;n nh&agrave;; x&aacute;c nhận t&igrave;nh trạng h&ocirc;n nh&acirc;n;&hellip;</p>\r\n\r\n<p>Theo quy định Luật Cư tr&uacute; năm 2020, kể từ ng&agrave;y 01/7/2022, mọi th&ocirc;ng tin li&ecirc;n quan đến cư tr&uacute; đều được cập nhật trong Cơ sở dữ liệu về cư tr&uacute;, được kết nối, chia sẻ với Cơ sở dữ liệu quốc gia về d&acirc;n cư. Do đ&oacute;, khi thu hồi Sổ hộ khẩu giấy, c&ocirc;ng d&acirc;n chỉ cần xuất tr&igrave;nh thẻ CCCD, cơ quan c&oacute; thẩm quyền sẽ tra cứu được c&aacute;c th&ocirc;ng tin cư tr&uacute;, nh&acirc;n th&acirc;n.</p>\r\n\r\n<p>Trong trường hợp, người bị thu hồi sổ hộ khẩu khi l&agrave;m c&aacute;c thủ tục quy định tại Khoản 2 Điều 26 Th&ocirc;ng tư 55/2021/TT-BCA nhưng vẫn cần giấy tờ chứng minh cư tr&uacute;, c&ocirc;ng d&acirc;n c&oacute; thể sử dụng &ldquo;Giấy x&aacute;c&nbsp;nhận th&ocirc;ng tin về cư tr&uacute;&rdquo;</p>\r\n\r\n<p>Nội dung của Giấy x&aacute;c nhận th&ocirc;ng tin về cư tr&uacute; sẽ bao gồm th&ocirc;ng tin về thời gian, địa điểm, h&igrave;nh thức đăng k&yacute; cư tr&uacute;.</p>\r\n\r\n<ol start=\"3\">\r\n	<li><strong>L&agrave;m Giấy x&aacute;c nhận th&ocirc;ng tin về cư tr&uacute; thế n&agrave;o?</strong></li>\r\n</ol>\r\n\r\n<p>C&ocirc;ng d&acirc;n c&oacute; quyền y&ecirc;u cầu cấp&nbsp;Giấy x&aacute;c nhận th&ocirc;ng tin về cư tr&uacute; theo 02 c&aacute;ch sau đ&acirc;y:</p>\r\n\r\n<p>&ndash; &nbsp;Trực tiếp đến cơ quan đăng k&yacute; cư tr&uacute; trong cả nước kh&ocirc;ng phụ thuộc v&agrave;o nơi cư tr&uacute; của c&ocirc;ng d&acirc;n để đề nghị cấp x&aacute;c nhận th&ocirc;ng tin về cư tr&uacute;;</p>\r\n\r\n<p>&ndash; Gửi&nbsp;y&ecirc;u cầu x&aacute;c nhận th&ocirc;ng tin về cư tr&uacute; qua Cổng dịch vụ c&ocirc;ng quốc gia, Cổng dịch vụ c&ocirc;ng Bộ C&ocirc;ng an, Cổng dịch vụ c&ocirc;ng quản l&yacute; cư tr&uacute;.</p>\r\n\r\n<p>Thời gian cấp Giấy x&aacute;c nhận th&ocirc;ng tin cư tr&uacute;: Cơ quan c&oacute; thẩm quyền sẽ x&aacute;c nhận th&ocirc;ng tin về cư tr&uacute; bằng văn bản giấy hoặc văn bản điện tử trong 03 ng&agrave;y l&agrave;m việc.</p>\r\n\r\n<ol start=\"4\">\r\n	<li><strong>Hiệu lực của Giấy x&aacute;c nhận th&ocirc;ng tin cư tr&uacute;?</strong></li>\r\n</ol>\r\n\r\n<p>Giấy x&aacute;c nhận th&ocirc;ng tin cư tr&uacute; c&oacute; gi&aacute; trị trong<strong>&nbsp;06 th&aacute;ng</strong>&nbsp;kể từ ng&agrave;y cấp với c&aacute;c trường hợp:</p>\r\n\r\n<p>+ Nơi cư tr&uacute; của người kh&ocirc;ng c&oacute; cả nơi thường tr&uacute; v&agrave; nơi tạm tr&uacute; do kh&ocirc;ng đủ điều kiện đăng k&yacute; thường tr&uacute;, đăng k&yacute; tạm tr&uacute; l&agrave; nơi ở hiện tại của người đ&oacute;;</p>\r\n\r\n<p>+ Trường hợp kh&ocirc;ng c&oacute; địa điểm chỗ ở cụ thể th&igrave; nơi ở hiện tại được x&aacute;c định l&agrave; đơn vị h&agrave;nh ch&iacute;nh cấp x&atilde; nơi người đ&oacute; đang thực tế sinh sống.</p>\r\n\r\n<p>+ Người kh&ocirc;ng c&oacute; nơi thường tr&uacute;, nơi tạm tr&uacute; phải khai b&aacute;o th&ocirc;ng tin về cư tr&uacute; với cơ quan đăng k&yacute; cư tr&uacute; tại nơi ở hiện tại.</p>\r\n\r\n<p>Giấy x&aacute;c nhận th&ocirc;ng tin cư tr&uacute; c&oacute; gi&aacute; trị trong<strong>&nbsp;30 ng&agrave;y</strong>&nbsp;kể từ ng&agrave;y cấp với c&aacute;c trường hợp x&aacute;c nhận th&ocirc;ng tin về cư tr&uacute;.</p>\r\n\r\n<p>Nếu c&ocirc;ng d&acirc;n thay đổi, điều chỉnh th&ocirc;ng tin về cư tr&uacute; v&agrave; được cập nhật tr&ecirc;n Cơ sở dữ liệu về cư tr&uacute; th&igrave; Giấy x&aacute;c nhận th&ocirc;ng tin về cư tr&uacute; hết gi&aacute; trị kể từ thời điểm thực hiện thay đổi.</p>\r\n', 1),
(8, 'Luật sư tư vấn Doanh nghiệp', 'Luật sư tư vấn nhượng quyền thương mại', 'download.png', '2023-03-13 10:24:51', '<h6><span style=\"font-size:14px\"><strong>I. Cơ sở ph&aacute;p l&yacute;</strong></span></h6>\r\n\r\n<ul>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Luật Thương mại năm 2005</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Nghị định số 35/2006/NĐ-CP</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Nghị định số 120/2011/NĐ-CP</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Th&ocirc;ng tư số 09/2006/TT-BTM</span></h6>\r\n	</li>\r\n</ul>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>II. Luật sư tư vấn</strong></span></h6>\r\n\r\n<ol>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\"><strong>Một số kh&aacute;i niệm</strong></span></h6>\r\n	</li>\r\n</ol>\r\n\r\n<h6><span style=\"font-size:14px\">B&ecirc;n nhượng quyền: L&agrave; thương nh&acirc;n cấp quyền thương mại</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">B&ecirc;n nhận quyền: L&agrave; thương nh&acirc;n được nhận quyền thương mại</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Nhượng quyền thương mại l&agrave; hoạt động thương mại, theo đ&oacute; b&ecirc;n nhượng quyền cho ph&eacute;p v&agrave; y&ecirc;u cầu b&ecirc;n nhận quyền tự m&igrave;nh tiến h&agrave;nh việc mua b&aacute;n h&agrave;ng ho&aacute;, cung ứng dịch vụ theo c&aacute;c điều kiện sau đ&acirc;y:</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">i) Việc mua b&aacute;n h&agrave;ng ho&aacute;, cung ứng dịch vụ được tiến h&agrave;nh theo c&aacute;ch thức tổ chức kinh doanh do b&ecirc;n nhượng quyền quy định v&agrave; được gắn với nh&atilde;n hiệu h&agrave;ng ho&aacute;, t&ecirc;n thương mại, b&iacute; quyết kinh doanh, khẩu hiệu kinh doanh, biểu tượng kinh doanh, quảng c&aacute;o của b&ecirc;n nhượng quyền;</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">ii) B&ecirc;n nhượng quyền c&oacute; quyền kiểm so&aacute;t v&agrave; trợ gi&uacute;p cho b&ecirc;n nhận quyền trong việc điều h&agrave;nh c&ocirc;ng việc kinh doanh.</span></h6>\r\n\r\n<ol start=\"2\">\r\n	<li>\r\n	<h6><span style=\"font-size:14px\"><strong>Hợp đồng nhượng quyền thương mại</strong></span></h6>\r\n	</li>\r\n</ol>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>a. Nội dung của hợp đồng nhượng quyền thương mại</strong></span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Trong trường hợp c&aacute;c b&ecirc;n lựa chọn &aacute;p dụng luật Việt Nam, hợp đồng nhượng quyền thương mại c&oacute; thể c&oacute; c&aacute;c nội dung chủ yếu sau đ&acirc;y:</span></h6>\r\n\r\n<ul>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Nội dung của quyền thương mại.</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Quyền, nghĩa vụ của B&ecirc;n nhượng quyền.</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Quyền, nghĩa vụ của B&ecirc;n nhận quyền.</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Gi&aacute; cả, ph&iacute; nhượng quyền định kỳ v&agrave; phương thức thanh to&aacute;n.</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Thời hạn hiệu lực của hợp đồng.</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Gia hạn, chấm dứt hợp đồng v&agrave; giải quyết tranh chấp.</span></h6>\r\n	</li>\r\n</ul>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>b. Ng&ocirc;n ngữ của hợp đồng nhượng quyền thương mại:&nbsp;</strong>Hợp đồng nhượng quyền thương mại phải được lập bằng tiếng Việt. Trường hợp nhượng quyền từ Việt Nam ra nước ngo&agrave;i, ng&ocirc;n ngữ của hợp đồng nhượng quyền thương mại do c&aacute;c b&ecirc;n thoả thuận.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>c. Thời hạn của hợp đồng nhượng quyền thương mại</strong><strong>:&nbsp;</strong>do c&aacute;c b&ecirc;n thoả thuận</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>d. Thời điểm c&oacute; hiệu lực của hợp đồng</strong><strong>:&nbsp;</strong>Hợp đồng nhượng quyền thương mại c&oacute; hiệu lực từ thời điểm giao kết trừ trường hợp c&aacute;c b&ecirc;n c&oacute; thoả thuận kh&aacute;c. Nếu trong hợp đồng nhượng quyền thương mại c&oacute; phần nội dung về chuyển giao quyền sử dụng đối tượng sở hữu tr&iacute; tuệ th&igrave; phần đ&oacute; c&oacute; hiệu lực theo quy định của ph&aacute;p luật về sở hữu tr&iacute; tuệ.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>3. Đăng k&yacute; hoạt động nhượng quyền thương mại</strong></span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>a. Chủ thể đăng k&yacute; nhượng quyền thương mại</strong></span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Trước khi tiến h&agrave;nh hoạt động nhượng quyền thương mại, thương nh&acirc;n Việt Nam hoặc thương nh&acirc;n nước ngo&agrave;i dự kiến nhượng quyền phải đăng k&yacute; hoạt động nhượng quyền thương mại với cơ quan c&oacute; thẩm quyền, trừ c&aacute;c trường hợp sau:</span></h6>\r\n\r\n<ul>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Nhượng quyền trong nước;</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Nhượng quyền thương mại từ Việt Nam ra nước ngo&agrave;i.</span></h6>\r\n	</li>\r\n</ul>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>b. Cơ quan c&oacute; thẩm quyền:&nbsp;</strong>Bộ C&ocirc;ng thương</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\"><strong>c. Th&agrave;nh phần hồ sơ:</strong></span></h6>\r\n\r\n<ul>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Đơn đăng k&yacute; hoạt động nhượng quyền thương mại</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Bản giới thiệu về nhượng quyền thương mại</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Bản sao Giấy chứng nhận đăng k&yacute; kinh doanh hoặc giấy tờ c&oacute; gi&aacute; trị tương đương của thương nh&acirc;n nước ngo&agrave;i được cơ quan c&oacute; thẩm quyền nơi thương nh&acirc;n nước ngo&agrave;i th&agrave;nh lập x&aacute;c nhận trong trường hợp nhượng quyền thương mại từ nước ngo&agrave;i v&agrave;o Việt Nam</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Bản sao c&oacute; c&ocirc;ng chứngvăn bằng bảo hộ quyền sở hữu c&ocirc;ng nghiệp tại Việt Nam hoặc tại nước ngo&agrave;i trong trường hợp c&oacute; chuyển giao quyền sử dụng c&aacute;c đối tượng sở hữu c&ocirc;ng nghiệp đ&atilde; được cấp văn bằng bảo hộ;</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Giấy tờ chứng minh sự chấp thuận về việc cho ph&eacute;p nhượng quyền lại của b&ecirc;n nhượng quyền ban đầu trong trường hợp thương nh&acirc;n đăng k&yacute; hoạt động nhượng quyền l&agrave; b&ecirc;n nhượng quyền thứ cấp.</span></h6>\r\n	</li>\r\n</ul>\r\n\r\n<h6><span style=\"font-size:14px\"><strong><em><u>Lưu &yacute;:</u></em></strong>&nbsp;Đối với c&aacute;c trường hợp kh&ocirc;ng phải đăng k&yacute; nhượng quyền phải thực hiện chế độ b&aacute;o c&aacute;o Sở C&ocirc;ng Thương.</span></h6>\r\n', 1),
(9, 'Luật sư tư vấn Đất đai', 'Đất “thổ cư” có phải là “đất ở” không?', 'Untitled291.png', '2023-03-13 10:25:39', '<p>L&acirc;u nay, người d&acirc;n sử dụng thuật ngữ &ldquo;thổ cư&rdquo; để chỉ &ldquo;đất ở&rdquo; do hiểu theo &acirc;m H&aacute;n &ndash; Việt &ldquo;thổ&rdquo; l&agrave; &ldquo;đất&rdquo;, &ldquo;cư&rdquo; l&agrave; &ldquo;ở&rdquo;. Tuy nhi&ecirc;n, ph&aacute;p luật Đất đai v&agrave; c&aacute;c văn bản hướng dẫn chỉ sử dụng thuật ngữ &ldquo;đất ở&rdquo; để ph&acirc;n loại đất, mục đ&iacute;ch sử dụng đất chứ kh&ocirc;ng d&ugrave;ng thuật ngữ &ldquo;thổ cư&rdquo;, do đ&oacute; kh&ocirc;ng thể khẳng định &ldquo;thổ cư&rdquo; l&agrave; &ldquo;đất ở&rdquo;. Trường hợp giấy tờ về quyền sử dụng đất v&agrave; sổ đỏ x&aacute;c định mục đ&iacute;ch sử dụng đất l&agrave; &ldquo;thổ cư&rdquo; th&igrave; phải giải quyết ra sao? Về vấn đề n&agrave;y, tr&ecirc;n cơ sở c&aacute;c quy định của ph&aacute;p luật c&oacute; li&ecirc;n quan, luật sư CMA c&oacute; quan điểm như sau:</p>\r\n\r\n<ol>\r\n	<li><strong>Đất thổ cư l&agrave; g&igrave;?</strong></li>\r\n</ol>\r\n\r\n<p>Theo quy định tại mục II của Quyết định số 339-VP/ĐKRĐ ng&agrave;y 27/8/1977 của Tổng cục Thống k&ecirc; ban h&agrave;nh Danh mục phiếu điều tra v&agrave; biểu tổng hợp v&agrave; bản giải th&iacute;ch hướng dẫn c&aacute;ch ghi ch&eacute;p tổng hợp kết quả điều tra đất th&igrave;:</p>\r\n\r\n<p><em>&ldquo;Cột 10 &ndash; Thổ cư: ghi những khu đất để x&acirc;y cất nh&agrave; ở, nh&agrave; tắm, nh&agrave; bếp, nh&agrave; vệ sinh, s&acirc;n, đường đi lại, chuồng trại chăn nu&ocirc;i gia đ&igrave;nh&rdquo;.</em></p>\r\n\r\n<p>Theo quy định tại Quyết định số 56-ĐKTK ng&agrave;y 5/11/1981 của Tổng cục Quản l&yacute; ruộng đất ban h&agrave;nh bản quy định về thủ tục đăng k&yacute; thống k&ecirc; ruộng đất trong cả nước th&igrave;</p>\r\n\r\n<p><strong><em>&ldquo;Loại đất khu vực thổ cư&rdquo; c&oacute; k&yacute; hiệu chữ &ldquo;T&rdquo;</em></strong><em>&nbsp;<strong>l&agrave; đất x&acirc;y dựng trong c&aacute;c khu d&acirc;n cư th&agrave;nh thị v&agrave; n&ocirc;ng th&ocirc;n</strong>, trừ thổ canh, ao hồ, đường đi lại trong th&ocirc;n x&oacute;m đ&atilde; đo đạc t&aacute;ch ra;&nbsp;<strong>bao gồm: đất ở v&agrave; đất x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh phục vụ lợi &iacute;ch to&agrave;n d&acirc;n trong c&aacute;c lĩnh vực văn h&oacute;a, y tế gi&aacute;o dục, thể dục thể thao, nghỉ m&aacute;t, du lịch; c&aacute;c trụ sở cơ quan, đo&agrave;n thể, t&ocirc;n gi&aacute;o, những kho t&agrave;ng, cơ sở n&ocirc;ng l&acirc;m nghiệp như chuồng trại chăn nu&ocirc;i, trạm sửa chữa</strong>&hellip;</em></p>\r\n\r\n<p>Như vậy, theo c&aacute;c quy định tr&ecirc;n th&igrave; &ldquo;thổ cư&rdquo; l&agrave; loại đất sử dụng x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh bao gồm: Ở hoặc phục vụ c&aacute;c hoạt động, mục đ&iacute;ch kh&aacute;c của con người như sản xuất, l&agrave;m việc, đi lại &hellip;..</p>\r\n\r\n<ol start=\"2\">\r\n	<li><strong>Sự nhầm lẫn giữa &ldquo;đất ở&rdquo; v&agrave; &ldquo;thổ cư&rdquo;</strong></li>\r\n</ol>\r\n\r\n<p>L&acirc;u nay, người d&acirc;n sử dụng thuật ngữ &ldquo;thổ cư&rdquo; để chỉ &ldquo;đất ở&rdquo; do hiểu theo &acirc;m H&aacute;n &ndash; Việt &ldquo;thổ&rdquo; l&agrave; &ldquo;đất&rdquo;, &ldquo;cư&rdquo; l&agrave; &ldquo;ở&rdquo;. Tuy nhi&ecirc;n, qu&aacute; tr&igrave;nh thống nhất quản l&yacute;, sử dụng đất đai từ năm 1987 đến nay, Luật Đất đai v&agrave; c&aacute;c văn bản quy phạm ph&aacute;p luật chỉ sử dụng thuật ngữ &ldquo;đất ở&rdquo; để ph&acirc;n loại đất, mục đ&iacute;ch sử dụng đất chứ kh&ocirc;ng d&ugrave;ng thuật ngữ &ldquo;thổ cư&rdquo;. Đ&oacute; l&agrave; c&aacute;ch m&agrave; c&aacute;c nh&agrave; l&agrave;m luật ưu ti&ecirc;n chọn kh&aacute;i niệm thuần Việt trong Luật đất đai.</p>\r\n\r\n<p>Ng&agrave;y 03/8/1989, Li&ecirc;n Tổng cục Quản l&yacute; ruộng đất v&agrave; Tổng cục Thống k&ecirc; ban h&agrave;nh Quyết định số 237/QĐ-LB t&aacute;ch loại&nbsp;<strong><em>&ldquo;Đất khu vực thổ cư</em></strong>&rdquo; trước đ&acirc;y th&agrave;nh&nbsp;<strong><em>&ldquo;Đất chuy&ecirc;n d&ugrave;ng&rdquo;</em></strong>&nbsp;(bao gồm Đất x&acirc;y dựng, Đất đường giao th&ocirc;ng, Đất thủy lợi&hellip;) v&agrave; &nbsp;<strong><em>&ldquo;Đất khu d&acirc;n cư&rdquo;</em></strong>&nbsp;(gồm Đất ở th&agrave;nh thị v&agrave; Đất ở n&ocirc;ng th&ocirc;n).</p>\r\n\r\n<p>Tại quyết định số 499-QĐ/ĐC ng&agrave;y 27/7/1995, k&yacute; hiệu bằng chữ &ldquo;T&rdquo; để quản l&yacute; loại đất &ldquo; thổ cư&rdquo; trước đ&acirc;y vẫn được sử dụng để quản l&yacute; nh&oacute;m &ldquo;đất ở&rdquo; gồm &ldquo;đất ở đ&ocirc; thị&rdquo; v&agrave; &ldquo;đất ở n&ocirc;ng th&ocirc;n&rdquo; trong Sổ mục k&ecirc;.</p>\r\n\r\n<p>Mặc d&ugrave; kh&ocirc;ng c&ograve;n sử dụng thuật ngữ &ldquo;thổ cư&rdquo; trong quản l&yacute;, ph&acirc;n loại đất nhưng trong c&aacute;c hồ sơ t&agrave;i liệu về quản l&yacute; đất trong lĩnh vực đất đai như Sổ mục k&ecirc;, bi&ecirc;n bản x&eacute;t duyệt của Hội đồng đăng k&yacute; ruộng đất v&agrave; cả Giấy chứng nhận quyền sử dụng đất cấp cho người d&acirc;n sau n&agrave;y, cơ quan c&oacute; thẩm quyền vẫn x&aacute;c định mục đ&iacute;ch sử dụng đất, loại đất l&agrave; &ldquo;thổ cư&rdquo;, &ldquo;TC&rdquo; cho diện t&iacute;ch đất c&oacute; nh&agrave; ở, vườn, ao, &hellip; trong khi người d&acirc;n th&igrave; cho rằng to&agrave;n bộ thửa đất l&agrave; &ldquo;đất ở&rdquo;. Qua đ&oacute; đ&atilde; xuất hiện nhiều quan điểm chưa thống nhất trong c&aacute;ch hiểu v&agrave; &aacute;p dụng c&aacute;c quy định ph&aacute;p luật hiện h&agrave;nh.</p>\r\n\r\n<p>Thậm ch&iacute;, tại địa b&agrave;n c&aacute;c tỉnh miền Trung, khi thực hiện Luật Đất đai năm 1993, Nghị định số 64/CP ng&agrave;y 27/9/1993 của Ch&iacute;nh phủ về việc giao đất n&ocirc;ng nghiệp cho hộ gia đ&igrave;nh, c&aacute; nh&acirc;n sử dụng ổn định l&acirc;u d&agrave;i v&agrave;o mục đ&iacute;ch sản xuất n&ocirc;ng nghiệp th&igrave;&nbsp;ch&iacute;nh quyền nhiều tỉnh&nbsp;l&uacute;c bấy giờ đ&atilde; thực hiện phương &aacute;n&nbsp;giao đất n&ocirc;ng nghiệp nhưng lại&nbsp;<a href=\"https://tuvanluathaiphong.com/cap-so-do-chung-cho-dat-o-va-dat-nong-nghiep/\">cấp chung một Giấy chứng nhận cho cả thửa đất sản xuất n&ocirc;ng nghiệp&nbsp;v&agrave; thửa đất &nbsp;ở c&oacute; vườn, ao;</a>&nbsp;kh&ocirc;ng x&aacute;c định ranh giới cụ thể từng thửa đất m&agrave; ghi gộp l&agrave; Thổ&nbsp;cư, &ldquo;T&rdquo; hoặc đất ở v&agrave; vườn với thời hạn sử dụng đất l&acirc;u d&agrave;i.&nbsp;<a href=\"https://tuvanluathaiphong.com/dat-tho-cu-co-phai-la-dat-o-khong/#_ftn1\" name=\"_ftnref1\">[1]</a></p>\r\n\r\n<ol start=\"3\">\r\n	<li><strong>Trường hợp giấy tờ về quyền sử dụng đất v&agrave; Sổ đỏ ghi mục đ&iacute;ch sử dụng đất l&agrave; &ldquo;thổ cư&rdquo; th&igrave; phải giải quyết ra sao.</strong></li>\r\n</ol>\r\n\r\n<p>Việc x&aacute;c định loại đất &ldquo;thổ cư&rdquo; tr&ecirc;n giấy tờ về quyền sử dụng đất, Giấy chứng nhận quyền sử dụng đất đ&atilde; cấp c&oacute; phải l&agrave; &ldquo;đất ở&rdquo; hay kh&ocirc;ng g&acirc;y ảnh hưởng lớn đến quyền v&agrave; lợi &iacute;ch hợp ph&aacute;p của chủ sử dụng đất.</p>\r\n\r\n<p>Theo quy định của ph&aacute;p luật đất đai hiện h&agrave;nh, việc x&aacute;c định loại đất &ldquo;thổ cư&rdquo; tr&ecirc;n giấy tờ về quyền sử dụng đất, Giấy chứng nhận quyền sử dụng đất đ&atilde; cấp c&oacute; phải l&agrave; &ldquo;đất ở&rdquo; hay kh&ocirc;ng cần căn cứ v&agrave;o giấy tờ về quyền sử dụng đất quy định tại Khoản 1, 2 v&agrave; 3 Điều 100 Luật Đất đai năm 2013 v&agrave; điều 18 nghị định 43/2014/NĐ-CP ghi loại đất &ldquo;thổ cư&rdquo; &ldquo;TC&rdquo;.</p>\r\n\r\n<ul>\r\n	<li><strong>Một số quan điểm</strong></li>\r\n</ul>\r\n\r\n<p><strong>&ndash; &Yacute; kiến thứ nhất</strong></p>\r\n\r\n<p>Hiện nay, cơ quan cấp Giấy chứng nhận quyền sử dụng đất, thu hồi đất thường x&aacute;c định đất &ldquo;thổ cư&rdquo; l&agrave; &ldquo;đất ở c&oacute; vườn ao kh&ocirc;ng ghi r&otilde; diện t&iacute;ch đất ở&rdquo; v&agrave; x&aacute;c định phần đất vượt hạn mức đất ở theo điều 103, 143, 144 luật đất đai 2013 v&agrave; điều 24 nghị định 43/2014/NĐ-CP l&agrave; đất trồng c&acirc;y l&acirc;u l&acirc;u năm, c&acirc;y h&agrave;ng năm,v.v&hellip;</p>\r\n\r\n<p>Tr&ecirc;n thực tế, gi&aacute; đất ở v&agrave; gi&aacute; đất n&ocirc;ng nghiệp c&oacute; gi&aacute; trị ch&ecirc;nh lệch lớn n&ecirc;n người sử dụng đất li&ecirc;n tục khởi kiện, khiếu nại để được c&ocirc;ng nhận phần diện t&iacute;ch đất vượt hạn mức l&agrave; đất ở</p>\r\n\r\n<p><strong>&ndash; &Yacute; kiến thứ hai</strong></p>\r\n\r\n<p>Căn cứ khoản 2 Điều 24&nbsp;Nghị định 43/2014/NĐ-CP quy định:&nbsp;<em>&ldquo;<strong>Loại giấy tờ l&agrave;m căn cứ x&aacute;c định diện t&iacute;ch đất ở</strong>&nbsp;theo quy định tại &hellip; Điều 103 của Luật Đất đai&nbsp;<strong>l&agrave; giấy tờ c&oacute; thể hiện một hoặc nhiều mục đ&iacute;ch</strong>&nbsp;nhưng trong đ&oacute; c&oacute; mục đ&iacute;ch để l&agrave;m nh&agrave; ở, đất ở hoặc&nbsp;<strong>thổ cư</strong>.&rdquo;</em></p>\r\n\r\n<p>Khoản 2 điều 45 nghị định 181/2004/NĐ-CP quy định<em>: &ldquo;Trường hợp đất ở c&oacute; vườn, ao trong c&ugrave;ng thửa đất c&oacute; nh&agrave; ở &nbsp;thuộc khu d&acirc;n cư được sử dụng trước ng&agrave;y 18 th&aacute;ng 12 năm 1980 m&agrave; trong hồ sơ địa ch&iacute;nh hoặc c&aacute;c loại giấy tờ về quyền sử dụng đất &hellip;.&nbsp;<strong>c&oacute; ghi nhận r&otilde; ranh giới thửa đất ở (hoặc thổ cư)</strong>&nbsp;th&igrave; to&agrave;n bộ diện t&iacute;ch đất đ&oacute; được x&aacute;c định l&agrave; đất ở &hellip;&rdquo;</em></p>\r\n\r\n<p>Như vậy, người sử dụng đất c&oacute; c&aacute;c giấy tờ về quyền sử dụng đất hoặc Giấy chứng nhận quyền sử dụng đất đ&atilde; cấp x&aacute;c định mục đ&iacute;ch sử dụng đất &ldquo;thổ cư&rdquo; th&igrave; to&agrave;n bộ diện t&iacute;ch đất &ldquo;thổ cư&rdquo; l&agrave; &ldquo;đất ở&rdquo;.</p>\r\n\r\n<p>Luật sư CMA đồng &yacute; với &yacute; kiến thứ 2, trước thời điểm Hiến ph&aacute;p năm 1980 ra đời, nước ta chưa c&oacute; luật đất đai để thống nhất quản l&yacute;, ph&acirc;n loại đất, hạn mức c&ocirc;ng nhận quyền sử dụng đất, gi&aacute; đất theo quy hoạch chung. Việc hiểu &ldquo;thổ cư&rdquo; l&agrave; &ldquo;kh&ocirc;ng x&aacute;c định r&otilde; diện t&iacute;ch đất ở&rdquo; l&agrave; kh&ocirc;ng ph&ugrave; hợp với thời điểm cấp giấy tờ về quyền sử dụng đất cho chủ sử dụng đất, g&acirc;y thiệt hại lớn cho người sử dụng đất, dẫn đến tranh chấp, khiếu kiện k&eacute;o d&agrave;i.</p>\r\n\r\n<p>Thực tế tiếp cận c&aacute;c giấy tờ về quyền sử dụng đất như sổ mục k&ecirc; lập trước ng&agrave;y 18/12/1980 hoặc c&aacute;c giấy tờ do cơ quan c&oacute; thẩm quyền thuộc chế độ cũ cấp cho người sử dụng đất như Bằng kho&aacute;n điển thổ của Ch&iacute;nh phủ Việt Nam D&acirc;n chủ Cộng ho&agrave; &hellip;. th&ocirc;ng tin loại đất mục đ&iacute;ch sử dụng đất trong c&aacute;c loại giấy tờ n&agrave;y thường chỉ ghi mục đ&iacute;ch sử dụng đất l&agrave; &ldquo;thổ cư&rdquo; do thời điểm cấp giấy tờ Bằng kho&aacute;n điền thổ kh&ocirc;ng c&oacute; quy định về ph&acirc;n loại đất, hạn mức c&ocirc;ng nhận quyền sử dụng đất.</p>\r\n\r\n<p><em>V&iacute; dụ: Bằng kho&aacute;n số 133 Lạch &ndash; Tray A, khu H tờ 5 thửa số 99, Trạch &ndash; chủ: Nguyễn Văn A, Diện t&iacute;ch: (Nh&agrave; 1 tầng: 12m<sup>2</sup>, nh&agrave; l&aacute; 39m<sup>2</sup>, thổ cư: 983m<sup>2</sup>) do Ty Địa ch&iacute;nh v&agrave; C&ocirc;ng thổ th&agrave;nh phố Hải Ph&ograve;ng cấp ng&agrave;y 27/2/1952.</em></p>\r\n\r\n<p>Từ những ph&acirc;n t&iacute;ch tr&ecirc;n, luật sư CMA cho rằng:</p>\r\n\r\n<p><strong>Trường hợp người sử dụng đất c&oacute; giấy tờ về quyền sử dụng đất theo khoản 1,2,3 điều 100 luật Đất đai 2013 v&agrave; điều 18 nghị định 43/2014/NĐ-CP m&agrave; giấy tờ đ&oacute; c&oacute; ghi mục đ&iacute;ch sử dụng đất , loại đất &ldquo;thổ cư&rdquo;, &ldquo;TC&rdquo;</strong>&nbsp;th&igrave; khi c&ocirc;ng nhận quyền sử dụng đất, khi hồi đất, cơ quan chức năng cần x&aacute;c định to&agrave;n bộ diện t&iacute;ch đất &ldquo;thổ cư&rdquo; l&agrave; &ldquo;đất ở&rdquo; để bảo đảm quyền lợi cho người sử dụng đất.</p>\r\n\r\n<p><strong>Trường hợp giấy chứng nhận quyền sử dụng đất đ&atilde; cấp chỉ ghi mục đ&iacute;ch sử dụng l&agrave; &ldquo;thổ cư&rdquo;</strong>. Người sử dụng đất cần l&agrave;m đơn phản &aacute;nh gửi cơ quan c&oacute; thẩm quyền cấp Giấy chứng nhận đ&atilde; cấp lần đầu kiểm tra, r&agrave; so&aacute;t thu hồi Giấy chứng nhận đ&atilde; cấp (thuộc trường hợp cấp kh&ocirc;ng đ&uacute;ng mục đ&iacute;ch sử dụng đất, thời hạn sử dụng đất theo quy định tại Điểm d, Khoản 2, Điều 106 Luật Đất đai năm 2013; Điều 87 Nghị định số: 43/2014/NĐ-CP ng&agrave;y 15/5/2014 của Ch&iacute;nh phủ) để&nbsp;cấp lại Giấy chứng nhận, x&aacute;c định lại diện t&iacute;ch đất &ldquo;thổ cư&rdquo;&nbsp;cho ph&ugrave; hợp&nbsp;theo quy định.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://tuvanluathaiphong.com/dat-tho-cu-co-phai-la-dat-o-khong/#_ftnref1\" name=\"_ftn1\"><strong>[1]</strong></a>&nbsp;Một số vấn đề nhận thức về &ldquo;đất thổ cư&rdquo; trong giải quyết &aacute;n h&agrave;nh ch&iacute;nh li&ecirc;n quan đến đất đai Nguyễn Xu&acirc;n Thanh &ndash; Trang th&ocirc;ng tin điện tử Viện kiểm s&aacute;t nh&acirc;n d&acirc;n cấp cao tại Đ&agrave; Nẵng ng&agrave;y 25/7/2022</p>\r\n', 1);
INSERT INTO `luatsutuvan` (`id`, `demuc`, `tieude`, `anh`, `thoigiandang`, `noidung`, `taikhoanluatsu_id`) VALUES
(10, 'Luật sư tư vấn Hợp đồng', 'Chế tài buộc thực hiện đúng hợp đồng', 'cuoc-hop-dong-gop-768x495.png', '2023-03-13 10:26:14', '<h6><span style=\"font-size:14px\">Buộc thực hiện nghĩa vụ n&oacute;i chung, buộc thực hiện đ&uacute;ng hợp đồng n&oacute;i ri&ecirc;ng l&agrave; một trong c&aacute;c phương thức bảo vệ quyền, lợi &iacute;ch hợp ph&aacute;p của một b&ecirc;n khi c&oacute; sự vi phạm của b&ecirc;n c&ograve;n lại.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Theo quy định tại khoản 4 điều 11 Bộ luật d&acirc;n sự 2015 th&igrave; Buộc thực hiện đ&uacute;ng hợp đồng l&agrave; một trong c&aacute;c biện ph&aacute;p bảo vệ quyền d&acirc;n sự. Việc buộc thực hiện đ&uacute;ng hợp đồng c&oacute; thể thực hiện bằng việc: i) B&ecirc;n c&oacute; quyền đưa y&ecirc;u cầu buộc b&ecirc;n c&ograve;n lại thực hiện; ii) B&ecirc;n c&oacute; quyền y&ecirc;u cầu cơ quan chức năng buộc b&ecirc;n c&ograve;n lại thực hiện nghĩa vụ. Đối với c&aacute;c tranh chấp th&igrave; thường l&agrave; To&agrave; &aacute;n hoặc trọng t&agrave;i (nếu c&oacute; thoả thuận).</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Theo Điều 297 Luật Thương mại 2005, &ldquo;<em>Buộc thực hiện đ&uacute;ng hợp đồng l&agrave; việc b&ecirc;n bị vi phạm y&ecirc;u cầu b&ecirc;n vi phạm thực hiện đ&uacute;ng hợp đồng hoặc d&ugrave;ng c&aacute;c biện ph&aacute;p kh&aacute;c để hợp đồng được thực hiện v&agrave; b&ecirc;n vi phạm phải chịu chi ph&iacute; ph&aacute;t sinh</em>&rdquo;.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">C&oacute; thể liệt k&ecirc; một số trường hợp buộc thực hiện đ&uacute;ng hợp đồng như sau:</span></h6>\r\n\r\n<ol>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Vi phạm giao thiếu h&agrave;ng hoặc cung ứng dịch vụ kh&ocirc;ng đ&uacute;ng thỏa thuận trong hợp đồng th&igrave; phải buộc giao đủ h&agrave;ng hoặc cung ứng dịch vụ theo đ&uacute;ng thoả thuận trong hợp đồng. B&ecirc;n vi phạm kh&ocirc;ng được d&ugrave;ng h&agrave;ng h&oacute;a kh&aacute;c chủng loại, loại dịch vụ kh&aacute;c, d&ugrave;ng tiền kh&aacute;c để thay thế nếu kh&ocirc;ng được sự chấp thuận của b&ecirc;n bị vi phạm</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Vi phạm giao h&agrave;ng ho&aacute;, cung ứng dịch vụ chất lượng k&eacute;m so với thỏa thuận hợp đồng th&igrave; b&ecirc;n vi phạm phải loại trừ khuyết tật của h&agrave;ng ho&aacute;, thiếu s&oacute;t của dịch vụ hoặc giao h&agrave;ng kh&aacute;c thay thế, cung ứng dịch vụ theo đ&uacute;ng hợp đồng.</span></h6>\r\n	</li>\r\n</ol>\r\n\r\n<h6><span style=\"font-size:14px\">Trường hợp b&ecirc;n vi phạm kh&ocirc;ng thực hiện chế t&agrave;i buộc thực hiện hợp đồng th&igrave; b&ecirc;n bị vi phạm c&oacute; quyền mua h&agrave;ng hoặc nhận cung ứng dịch vụ của người kh&aacute;c c&ugrave;ng chủng loại h&agrave;ng h&oacute;a, dịch vụ ghi trong hợp đồng để thay thế, v&agrave; b&ecirc;n vi phạm phải trả khoản tiền ch&ecirc;nh lệch v&agrave; c&aacute;c chi ph&iacute; li&ecirc;n quan nếu c&oacute;; b&ecirc;n bị vi phạm c&oacute; thể tự sửa chữa h&agrave;ng h&oacute;a, thiếu s&oacute;t của dịch vụ v&agrave; được quyền b&ecirc;n vi phạm trả chi ph&iacute; hợp l&yacute;.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Buộc thực hiện đ&uacute;ng hợp đồng nhằm đảm bảo thực hiện tr&ecirc;n thực tế hợp đồng đ&atilde; k&iacute; kết. Trong nhiều trường hợp, c&aacute;c chế t&agrave;i kh&aacute;c như bồi thường thiệt hại hay phạt vi phạm kh&ocirc;ng thể thay thế lợi &iacute;ch từ việc thực hiện hợp đồng. Việc thực hiện đ&uacute;ng hợp đồng c&ograve;n thể hiện sự uy t&iacute;n của c&aacute;c b&ecirc;n giao kết hợp đồng, do đ&oacute; c&aacute;c b&ecirc;n phải ho&agrave;n th&agrave;nh nghĩa vụ của m&igrave;nh để bảo đảm quyền lợi cho ph&iacute;a b&ecirc;n kia.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Buộc thực hiện đ&uacute;ng hợp đồng l&agrave; một h&igrave;nh thức chế t&agrave;i được &aacute;p dụng phổ biến khi c&oacute; h&agrave;nh vi vi phạm. C&aacute;c h&agrave;nh vi vi phạm l&agrave; h&agrave;nh vi đ&atilde; kh&ocirc;ng thực hiện đ&uacute;ng những nghĩa vụ đ&atilde; cam kết:</span></h6>\r\n\r\n<ul>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Kh&ocirc;ng thực hiện c&aacute;c nghĩa vụ đ&atilde; cam kết trong hợp đồng.</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Thực hiện kh&ocirc;ng đ&uacute;ng c&aacute;c nghĩa vụ như đ&atilde; thỏa thuận.</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Thực hiện kh&ocirc;ng đầy đủ c&aacute;c nghĩa vụ đ&atilde; thỏa thuận.</span></h6>\r\n	</li>\r\n</ul>\r\n\r\n<h6><span style=\"font-size:14px\">Chế t&agrave;i n&agrave;y tương đương với tr&aacute;ch nhiệm phải thực hiện nghĩa vụ, với tr&aacute;ch nhiệm n&agrave;y, người vi phạm nghĩa vụ phải tiếp tục thực hiện nghĩa vụ theo y&ecirc;u cầu của b&ecirc;n kia. Loại tr&aacute;ch nhiệm n&agrave;y bao gồm:</span></h6>\r\n\r\n<ul>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Tr&aacute;ch nhiệm do kh&ocirc;ng thực hiện nghĩa vụ giao vật. (Điều 356 BLDS 2015)</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Tr&aacute;ch nhiệm do kh&ocirc;ng thực hiện hoặc thực hiện kh&ocirc;ng được thực hiện một c&ocirc;ng việc. (Điều 358 BLDS 2015)</span></h6>\r\n	</li>\r\n	<li>\r\n	<h6><span style=\"font-size:14px\">Tr&aacute;ch nhiệm do chậm tiếp nhận việc thực hiện nghĩa vụ d&acirc;n sự. (Điều 355 BLDS 2015)</span></h6>\r\n	</li>\r\n</ul>\r\n\r\n<h6><span style=\"font-size:14px\">Chế t&agrave;i n&agrave;y thể hiện r&otilde; trong nghĩa vụ chuyển giao t&agrave;i sản. Nghĩa vụ chuyển giao t&agrave;i sản l&agrave; nghĩa vụ phổ biến trong hợp đồng mua b&aacute;n t&agrave;i sản. B&ecirc;n c&oacute; nghĩa vụ chuyển giao t&agrave;i sản phải thực hiện nghĩa vụ của m&igrave;nh v&agrave; việc kh&ocirc;ng thực hiện nghĩa vụ chuyển giao t&agrave;i sản l&agrave; một trong c&aacute;c nguy&ecirc;n nh&acirc;n l&agrave;m ph&aacute;t sinh tr&aacute;ch nhiệm d&acirc;n sự do vi phạm hợp đồng.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Căn cứ theo c&aacute;c quy định tr&ecirc;n, trừ trường hợp c&aacute;c b&ecirc;n c&oacute; thỏa thuận kh&aacute;c th&igrave; b&ecirc;n bị vi phạm c&oacute; thể &aacute;p dụng đồng thời chế t&agrave;i buộc thực hiện hợp đồng, chế t&agrave;i bồi thường thiệt hại v&agrave; phạt vi phạm, kh&ocirc;ng được &aacute;p dụng c&aacute;c chế t&agrave;i kh&aacute;c. Nghĩa l&agrave; b&ecirc;n bị vi phạm nếu đ&atilde; &aacute;p dụng buộc thực hiện hợp đồng th&igrave; chỉ được &aacute;p dụng bồi thường thiệt hại v&agrave; phạt vi phạm nếu thỏa m&atilde;n c&aacute;c điều kiện &aacute;p dụng theo quy định của ph&aacute;p luật.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">B&ecirc;n bị vi phạm hợp đồng y&ecirc;u cầu b&ecirc;n vi phạm thực hiện đ&uacute;ng c&aacute;c nghĩa vụ đ&atilde; cam kết trong hợp đồng hoặc d&ugrave;ng c&aacute;c biện ph&aacute;p kh&aacute;c để hợp đồng được thực hiện v&agrave; mọi tổn thất ph&aacute;t sinh b&ecirc;n vi phạm sẽ phải chịu. B&ecirc;n bị vi phạm c&oacute; thể quyết định &aacute;p dụng chế t&agrave;i buộc thực hiện đ&uacute;ng hợp đồng trước khi sử dụng c&aacute;c chế t&agrave;i kh&aacute;c m&agrave; kh&ocirc;ng bị mất đi quyền y&ecirc;u cầu bồi thường thiệt hại hay phạt vi phạm hợp đồng. C&aacute;c chế t&agrave;i kh&aacute;c ch&iacute;nh l&agrave; tạm ngừng thực hiện hợp đồng, đ&igrave;nh chỉ thực hiện hợp đồng v&agrave; hủy bỏ hợp đồng.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Tuy nhi&ecirc;n, tại khoản 3 Điều 51 của Luật thương mại 2005 quy định như sau: &ldquo;b&ecirc;n mua c&oacute; bằng chứng về việc b&ecirc;n b&aacute;n đ&atilde; giao h&agrave;ng kh&ocirc;ng ph&ugrave; hợp với hợp đồng th&igrave; c&oacute; quyền tạm ngừng thanh to&aacute;n cho đến khi b&ecirc;n b&aacute;n đ&atilde; khắc phục sự kh&ocirc;ng ph&ugrave; hợp đ&oacute;&rdquo;. Theo điều luật n&agrave;y th&igrave; việc ngừng thanh to&aacute;n ch&iacute;nh l&agrave; tạm ngừng thực hiện hợp đồng. Như vậy trong trường hợp n&agrave;y b&ecirc;n mua c&oacute; thể c&ugrave;ng l&uacute;c &aacute;p dụng chế t&agrave;i buộc thực hiện đ&uacute;ng hợp đồng v&agrave; chế t&agrave;i tạm ngừng thực hiện hợp đồng. Đ&acirc;y l&agrave; m&acirc;u thuẫn trong c&aacute;c quy định của Luật Thương mại 2005.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">B&ecirc;n bị vi phạm hợp đồng &aacute;p dụng chế t&agrave;i buộc thực hiện đ&uacute;ng hợp đồng trong những trường hợp m&agrave; việc k&eacute;o d&agrave;i thời gian thực hiện hợp đồng kh&ocirc;ng ảnh hưởng đến lợi &iacute;ch kinh tế của m&igrave;nh. B&ecirc;n bị vi phạm kh&ocirc;ng thể lựa chọn &aacute;p dụng chế t&agrave;i buộc thực hiện đ&uacute;ng hợp đồng đối với những loại h&agrave;ng h&oacute;a như mang t&iacute;nh chất m&ugrave;a vụ, phụ thuộc v&agrave;o thời điểm th&iacute;ch hợp trong năm.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Cơ sở thực tiễn của chế t&agrave;i n&agrave;y ch&iacute;nh l&agrave; mục đ&iacute;ch k&iacute; kết hợp đồng thương mại, c&aacute;c b&ecirc;n khi tham gia k&iacute; kết hợp đồng đều mong muốn quyền v&agrave; nghĩa vụ đ&atilde; cam kết được thực hiện một c&aacute;ch thiện ch&iacute; v&agrave; đầy đủ nhằm đạt được lợi &iacute;ch kinh tế cho cả hai b&ecirc;n.</span></h6>\r\n\r\n<h6><span style=\"font-size:14px\">Tr&ecirc;n thực tế c&oacute; những trường hợp tiền phạt vi phạm hay tiền bồi thường thiệt hại kh&ocirc;ng thể thay thế được c&aacute;c lợi &iacute;ch từ việc thực hiện đồng. Trừ khi đ&atilde; cố gắng nhưng kh&ocirc;ng khắc phục được vi phạm, b&ecirc;n c&oacute; quyền mới &aacute;p dụng c&aacute;c biện ph&aacute;p chế t&agrave;i kh&aacute;c như hủy bỏ hợp đồng, phạt vi phạm (nếu hai b&ecirc;n c&oacute; thỏa thuận) hay y&ecirc;u cầu bồi thường thiệt hại (nếu c&oacute; thiệt hại xảy ra). Do đ&oacute;, so với c&aacute;c h&igrave;nh thức chế t&agrave;i kh&aacute;c, buộc thực hiện đ&uacute;ng hợp đồng l&agrave; chế t&agrave;i mang t&iacute;nh mềm dẻo, hạn chế tổn thất thiệt hại hơn nhiều so với c&aacute;c h&igrave;nh thức chế t&agrave;i kh&aacute;c</span></h6>\r\n', 1);

